					vl_od_pl_ard_av_sc	=	vl_od_pl_ard_av_sc_w,
					vl_od_pl_ard_eixo		=	vl_od_pl_ard_eixo_w,
					vl_od_pl_ard_esf		=	vl_od_pl_ard_esf_w,
					vl_od_pl_are_av_sc	=	vl_od_pl_are_av_sc_w,
					vl_od_pl_are_eixo		=	vl_od_pl_are_eixo_w,
					vl_od_pl_are_esf		=	vl_od_pl_are_esf_w,
					vl_oe_adic_av			=	vl_oe_adic_av_w,
					vl_oe_pl_ard_av_sc	=	vl_oe_pl_ard_av_sc_w,
					vl_oe_pl_ard_eixo		=	vl_oe_pl_ard_eixo_w,
					vl_oe_pl_ard_esf		=	vl_oe_pl_ard_esf_w,
					vl_oe_pl_are_av_sc	=	vl_oe_pl_are_av_sc_w,
					vl_oe_pl_are_eixo		=	vl_oe_pl_are_eixo_w,
					vl_oe_pl_are_esf		=	vl_oe_pl_are_esf_w,
					vl_od_pl_ard_cil		=	vl_od_pl_ard_cil_w,
					vl_oe_pl_ard_cil		=	vl_oe_pl_ard_cil_w,
					vl_od_pl_are_cil		=	vl_od_pl_are_cil_w,
					vl_oe_pl_are_cil		=	vl_oe_pl_are_cil_w,
					ie_plano_rdl_oe 		= coalesce(ie_plano_rdl_oe_w,ie_plano_rdl_oe),
					vl_ard_dp 				= vl_ard_dp_w,
					vl_od_pp_ard_av_sc 	= vl_od_pp_ard_av_sc_w,
					vl_od_pp_ard_eixo 	= vl_od_pp_ard_eixo_w,
					vl_od_pp_are_eixo 	= vl_od_pp_are_eixo_w,
					vl_oe_pp_ard_av_cc 	= vl_oe_pp_ard_av_cc_w,
					vl_oe_pp_ard_av_sc 	= vl_oe_pp_ard_av_sc_w,
					vl_oe_pp_ard_cil 		= vl_oe_pp_ard_cil_w,
					ie_plano_rdp_od 		= coalesce(ie_plano_rdp_od_w,ie_plano_rdp_od),
					ie_plano_rdp_oe 		= coalesce(ie_plano_rdp_oe_w,ie_plano_rdp_oe),
					ie_plano_rep_od 		= coalesce(ie_plano_rep_od_w,ie_plano_rep_od),
					vl_od_pp_are_av_sc 	= vl_od_pp_are_av_sc_w,
					vl_od_pp_are_cil 		= vl_od_pp_are_cil_w,
					vl_oe_pp_ard_eixo 	= vl_oe_pp_ard_eixo_w,
					vl_oe_pp_are_esf 		= vl_oe_pp_are_esf_w,
					vl_od_pp_are_av_cc 	= vl_od_pp_are_av_cc_w,
					vl_oe_pl_are_av_cc 	= vl_oe_pl_are_av_cc_w,
					vl_oe_pp_are_av_cc 	= vl_oe_pp_are_av_cc_w,
					vl_od_pl_ard_av_cc 	= vl_od_pl_ard_av_cc_w,
					vl_oe_ard_dnp 			= vl_oe_ard_dnp_w,
					vl_oe_pl_ard_av_cc 	= vl_oe_pl_ard_av_cc_w,
					vl_oe_pp_are_av_sc 	= vl_oe_pp_are_av_sc_w,
					vl_oe_pp_are_cil 		= vl_oe_pp_are_cil_w,
					ie_plano_rel_od 		= coalesce(ie_plano_rel_od_w,ie_plano_rel_od),
					vl_od_pp_ard_av_cc 	= vl_od_pp_ard_av_cc_w,
					vl_od_pp_ard_cil 		= vl_od_pp_ard_cil_w,
					vl_od_pp_ard_esf 		= vl_od_pp_ard_esf_w,
					vl_oe_pp_are_eixo 	= vl_oe_pp_are_eixo_w,
					ie_plano_rdl_od 		= coalesce(ie_plano_rdl_od_w,ie_plano_rdl_od),
					ie_plano_rel_oe 		= coalesce(ie_plano_rel_oe_w,ie_plano_rel_oe),
					ie_plano_rep_oe 		= coalesce(ie_plano_rep_oe_w,ie_plano_rep_oe),
					vl_od_ard_dnp 			= vl_od_ard_dnp_w,
					vl_od_pl_are_av_cc 	= vl_od_pl_are_av_cc_w,
					vl_od_pp_are_esf 		= vl_od_pp_are_esf_w,
					vl_oe_pp_ard_esf 		= vl_oe_pp_ard_esf_w,
					ds_ref_direto_od    = ds_ref_direto_od_w,
			        ds_ref_direto_oe    = ds_ref_direto_oe_w,
