														vl_od_adic_av,
														vl_od_pl_ard_av_sc,
														vl_od_pl_ard_eixo,
														vl_od_pl_ard_esf,
														vl_od_pl_are_av_sc,
														vl_od_pl_are_eixo,
														vl_od_pl_are_esf,
														vl_oe_adic_av,
														vl_oe_pl_ard_av_sc,
														vl_oe_pl_ard_eixo,
														vl_oe_pl_ard_esf,
														vl_oe_pl_are_av_sc,
														vl_oe_pl_are_eixo,
														vl_oe_pl_are_esf,
														vl_od_pl_ard_cil,	
														vl_oe_pl_ard_cil,
														vl_od_pl_are_cil,
														vl_oe_pl_are_cil,
														nr_seq_consulta_form,
														ie_plano_rdl_oe,
														vl_ard_dp,
														vl_od_pp_ard_av_sc,
														vl_od_pp_ard_eixo,
														vl_od_pp_are_eixo,
														vl_oe_pp_ard_av_cc,
														vl_oe_pp_ard_av_sc,
														vl_oe_pp_ard_cil,
														ie_plano_rdp_od,
														ie_plano_rdp_oe,
														ie_plano_rep_od,
														vl_od_pp_are_av_sc,
														vl_od_pp_are_cil,
														vl_oe_pp_ard_eixo,
														vl_oe_pp_are_esf,
														vl_od_pp_are_av_cc,
														vl_oe_pl_are_av_cc,
														vl_oe_pp_are_av_cc,
														vl_od_pl_ard_av_cc,
														vl_oe_ard_dnp,
														vl_oe_pl_ard_av_cc,
														vl_oe_pp_are_av_sc,
														vl_oe_pp_are_cil,
														ie_plano_rel_od,
														vl_od_pp_ard_av_cc,
														vl_od_pp_ard_cil,
														vl_od_pp_ard_esf,
														vl_oe_pp_are_eixo,
														ie_plano_rdl_od,
														ie_plano_rel_oe,
														ie_plano_rep_oe,
														vl_od_ard_dnp,
														vl_od_pl_are_av_cc,
														vl_od_pp_are_esf,
														vl_oe_pp_ard_esf,
														ie_refracao_sugerida,
														ds_ref_direto_od,
											            ds_ref_direto_oe,
											            ds_ref_consen_od,
											            ds_ref_consen_oe,
											            ds_ref_acomod_od,
											            ds_ref_acomod_oe
														)
			values (nr_sequencia_w,
														coalesce(dt_exame_w,clock_timestamp()),
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
														ds_observacao_w,
														clock_timestamp(),
														nm_usuario_w,
														clock_timestamp(),
														nm_usuario_w,
														coalesce(ie_receita_dinamica_w,'N'),
														coalesce(ie_receita_estatica_w,'N'),
														'A',
														nr_seq_consulta_p,
														vl_adicao_w,
														vl_adicao_oe_w,
														vl_od_adic_av_w,
														vl_od_pl_ard_av_sc_w,
														vl_od_pl_ard_eixo_w,
														vl_od_pl_ard_esf_w,
														vl_od_pl_are_av_sc_w,
														vl_od_pl_are_eixo_w,
														vl_od_pl_are_esf_w,
														vl_oe_adic_av_w,
														vl_oe_pl_ard_av_sc_w,
														vl_oe_pl_ard_eixo_w,
														vl_oe_pl_ard_esf_w,
														vl_oe_pl_are_av_sc_w,
														vl_oe_pl_are_eixo_w,
														vl_oe_pl_are_esf_w,
														vl_od_pl_ard_cil_w,	
														vl_oe_pl_ard_cil_w,
														vl_od_pl_are_cil_w,
														vl_oe_pl_are_cil_w,
														nr_seq_consulta_form_p,
														coalesce(ie_plano_rdl_oe_w,'N'),
														vl_ard_dp_w,
														vl_od_pp_ard_av_sc_w,
														vl_od_pp_ard_eixo_w,
														vl_od_pp_are_eixo_w,
														vl_oe_pp_ard_av_cc_w,
														vl_oe_pp_ard_av_sc_w,
														vl_oe_pp_ard_cil_w,
														coalesce(ie_plano_rdp_od_w,'N'),
														coalesce(ie_plano_rdp_oe_w,'N'),
														coalesce(ie_plano_rep_od_w,'N'),
														vl_od_pp_are_av_sc_w,
														vl_od_pp_are_cil_w,
														vl_oe_pp_ard_eixo_w,
														vl_oe_pp_are_esf_w,
														vl_od_pp_are_av_cc_w,
														vl_oe_pl_are_av_cc_w,
														vl_oe_pp_are_av_cc_w,
														vl_od_pl_ard_av_cc_w,
														vl_oe_ard_dnp_w,
														vl_oe_pl_ard_av_cc_w,
														vl_oe_pp_are_av_sc_w,
														vl_oe_pp_are_cil_w,
														coalesce(ie_plano_rel_od_w,'N'),
														vl_od_pp_ard_av_cc_w,
														vl_od_pp_ard_cil_w,
														vl_od_pp_ard_esf_w,
														vl_oe_pp_are_eixo_w,
														coalesce(ie_plano_rdl_od_w,'N'),
														coalesce(ie_plano_rel_oe_w,'N'),
														coalesce(ie_plano_rep_oe_w,'N'),
