														vl_od_pl_are_av_cc_w,
														vl_od_pp_are_esf_w,
														vl_oe_pp_ard_esf_w,
														'N',
														ds_ref_direto_od_w,
											            ds_ref_direto_oe_w,
											            ds_ref_consen_od_w,
											            ds_ref_consen_oe_w,
											            ds_ref_acomod_od_w,
											            ds_ref_acomod_oe_w);
		end if;												
	end if;												
end if;	

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
