SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_acuidade ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaAcuidade INOUT strRecTypeFormOft) AS $body$
DECLARE


cd_profissional_w		oft_acuidade_visual.cd_profissional%TYPE;
dt_registro_w			oft_acuidade_visual.dt_registro%TYPE;
vl_od_pl_av_sc_w		oft_acuidade_visual.vl_od_pl_av_sc%TYPE;
vl_od_pl_av_cc_w		oft_acuidade_visual.vl_od_pl_av_cc%TYPE;
vl_oe_pl_av_sc_w		oft_acuidade_visual.vl_oe_pl_av_sc%TYPE;
vl_oe_pl_av_cc_w		oft_acuidade_visual.vl_oe_pl_av_cc%TYPE;
vl_oe_pp_av_w			oft_acuidade_visual.vl_oe_pp_av%TYPE;
vl_od_pp_av_w			oft_acuidade_visual.vl_od_pp_av%TYPE;
ds_observacao_w		oft_acuidade_visual.ds_observacao%TYPE;
vl_od_pl_cor_sc_w		oft_acuidade_visual.vl_od_pl_cor_sc%TYPE;
vl_od_pl_con_sc_w		oft_acuidade_visual.vl_od_pl_con_sc%TYPE;
vl_od_pl_cor_cc_w		oft_acuidade_visual.vl_od_pl_cor_cc%TYPE;
vl_od_pl_con_cc_w		oft_acuidade_visual.vl_od_pl_con_cc%TYPE;
vl_oe_pl_cor_sc_w		oft_acuidade_visual.vl_oe_pl_cor_sc%TYPE;
vl_oe_pl_con_sc_w		oft_acuidade_visual.vl_oe_pl_con_sc%TYPE;
vl_oe_pl_cor_cc_w		oft_acuidade_visual.vl_oe_pl_cor_cc%TYPE;
vl_od_pp_con_sc_w		oft_acuidade_visual.vl_od_pp_con_sc%TYPE;
vl_od_pp_cor_sc_w		oft_acuidade_visual.vl_od_pp_cor_sc%TYPE;
vl_od_pp_cor_cc_w		oft_acuidade_visual.vl_od_pp_cor_cc%TYPE;
vl_od_pp_con_cc_w		oft_acuidade_visual.vl_od_pp_con_cc%TYPE;
vl_oe_pl_con_cc_w		oft_acuidade_visual.vl_oe_pl_con_cc%TYPE;
ie_tipo_w				oft_acuidade_visual.ie_tipo%TYPE;
dt_liberacao_w			oft_acuidade_visual.dt_liberacao%TYPE;
nm_usuario_w			usuario.nm_usuario%TYPE := wheb_usuario_pck.get_nm_usuario;
ds_erro_w				varchar(4000);

acuidade_form CURSOR FOR
	SELECT	a.*
	FROM		oft_acuidade_visual a,
				oft_consulta_formulario b
	WHERE		a.nr_seq_consulta_form 	=	b.nr_sequencia
	AND		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	AND		a.nr_seq_consulta			=	nr_seq_consulta_p
	AND		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') OR (a.nm_usuario = nm_usuario_w))
	AND		((coalesce(a.dt_inativacao::text, '') = '') OR (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	ORDER BY dt_registro;

acuidade_paciente CURSOR FOR
	SELECT	a.*
	FROM		oft_acuidade_visual a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	ORDER BY dt_registro;


BEGIN
begin

IF (coalesce(nr_seq_consulta_p,0) > 0) AND (vListaAcuidade.COUNT > 0) THEN
	if (ie_opcao_p = 'F') then
		FOR c_acuidade IN acuidade_form LOOP
			BEGIN
			cd_profissional_w		:=	c_acuidade.cd_profissional;
			dt_registro_w			:=	c_acuidade.dt_registro;
			vl_od_pl_av_sc_w		:=	c_acuidade.vl_od_pl_av_sc;
			vl_od_pl_av_cc_w		:=	c_acuidade.vl_od_pl_av_cc;
			vl_oe_pl_av_sc_w		:=	c_acuidade.vl_oe_pl_av_sc;
			vl_oe_pl_av_cc_w		:=	c_acuidade.vl_oe_pl_av_cc;
			vl_oe_pp_av_w			:=	c_acuidade.vl_oe_pp_av;
			vl_od_pp_av_w			:=	c_acuidade.vl_od_pp_av;
			ds_observacao_w		:=	c_acuidade.ds_observacao;
			vl_od_pl_cor_sc_w		:=	c_acuidade.vl_od_pl_cor_sc;
			vl_od_pl_con_sc_w		:=	c_acuidade.vl_od_pl_con_sc;
			vl_od_pl_cor_cc_w		:=	c_acuidade.vl_od_pl_cor_cc;
			vl_od_pl_con_cc_w		:=	c_acuidade.vl_od_pl_con_cc;
			vl_oe_pl_cor_sc_w		:=	c_acuidade.vl_oe_pl_cor_sc;
			vl_oe_pl_con_sc_w		:=	c_acuidade.vl_oe_pl_con_sc;
			vl_oe_pl_cor_cc_w		:=	c_acuidade.vl_oe_pl_cor_cc;
			vl_od_pp_con_sc_w		:=	c_acuidade.vl_od_pp_con_sc;
			vl_od_pp_cor_sc_w		:=	c_acuidade.vl_od_pp_cor_sc;
			vl_od_pp_cor_cc_w		:=	c_acuidade.vl_od_pp_cor_cc;
			vl_od_pp_con_cc_w		:=	c_acuidade.vl_od_pp_con_cc;
			vl_oe_pl_con_cc_w		:=	c_acuidade.vl_oe_pl_con_cc;
			ie_tipo_w				:=	c_acuidade.ie_tipo;
			dt_liberacao_w			:=	c_acuidade.dt_liberacao;
			END;
		END LOOP;
	else
		FOR c_acuidade IN acuidade_paciente LOOP
			BEGIN
			cd_profissional_w		:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_registro_w			:=	clock_timestamp();
			vl_od_pl_av_sc_w		:=	c_acuidade.vl_od_pl_av_sc;
			vl_od_pl_av_cc_w		:=	c_acuidade.vl_od_pl_av_cc;
			vl_oe_pl_av_sc_w		:=	c_acuidade.vl_oe_pl_av_sc;
			vl_oe_pl_av_cc_w		:=	c_acuidade.vl_oe_pl_av_cc;
			vl_oe_pp_av_w			:=	c_acuidade.vl_oe_pp_av;
			vl_od_pp_av_w			:=	c_acuidade.vl_od_pp_av;
			ds_observacao_w		:=	c_acuidade.ds_observacao;
			vl_od_pl_cor_sc_w		:=	c_acuidade.vl_od_pl_cor_sc;
			vl_od_pl_con_sc_w		:=	c_acuidade.vl_od_pl_con_sc;
			vl_od_pl_cor_cc_w		:=	c_acuidade.vl_od_pl_cor_cc;
			vl_od_pl_con_cc_w		:=	c_acuidade.vl_od_pl_con_cc;
			vl_oe_pl_cor_sc_w		:=	c_acuidade.vl_oe_pl_cor_sc;
			vl_oe_pl_con_sc_w		:=	c_acuidade.vl_oe_pl_con_sc;
			vl_oe_pl_cor_cc_w		:=	c_acuidade.vl_oe_pl_cor_cc;
			vl_od_pp_con_sc_w		:=	c_acuidade.vl_od_pp_con_sc;
			vl_od_pp_cor_sc_w		:=	c_acuidade.vl_od_pp_cor_sc;
			vl_od_pp_cor_cc_w		:=	c_acuidade.vl_od_pp_cor_cc;
			vl_od_pp_con_cc_w		:=	c_acuidade.vl_od_pp_con_cc;
			vl_oe_pl_con_cc_w		:=	c_acuidade.vl_oe_pl_con_cc;
			ie_tipo_w				:=	c_acuidade.ie_tipo;
			END;
		END LOOP;
	end if;


	FOR i IN 1..vListaAcuidade.COUNT LOOP
		BEGIN
		if (ie_opcao_p = 'F') or (vListaAcuidade[i].ie_obter_resultado = 'S') then
			vListaAcuidade[i].dt_liberacao	:= dt_liberacao_w;
			CASE UPPER(vListaAcuidade[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaAcuidade[i].ds_valor	:=	cd_profissional_w;
				WHEN 'DT_REGISTRO' THEN
					vListaAcuidade[i].dt_valor	:=	dt_registro_w;
				WHEN 'VL_OD_PL_AV_SC' THEN
					vListaAcuidade[i].ds_valor	:=	vl_od_pl_av_sc_w;
				WHEN 'VL_OD_PL_AV_CC' THEN
					vListaAcuidade[i].nr_valor	:= vl_od_pl_av_cc_w;
				WHEN 'VL_OE_PL_AV_SC' THEN
					vListaAcuidade[i].nr_valor	:= vl_oe_pl_av_sc_w;
				WHEN 'VL_OE_PL_AV_CC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_oe_pl_av_cc_w;
				WHEN 'VL_OE_PP_AV' THEN
					vListaAcuidade[i].nr_valor	:=	vl_oe_pp_av_w;
				WHEN 'VL_OD_PP_AV' THEN
					vListaAcuidade[i].nr_valor	:=	vl_od_pp_av_w;
				WHEN 'DS_OBSERVACAO' THEN
					vListaAcuidade[i].ds_valor	:=	ds_observacao_w;
				WHEN 'VL_OD_PL_COR_SC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_od_pl_cor_sc_w;
				WHEN 'VL_OD_PL_CON_SC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_od_pl_con_sc_w;
				WHEN 'VL_OD_PL_COR_CC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_od_pl_cor_cc_w;
				WHEN 'VL_OD_PL_CON_CC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_od_pl_con_cc_w;
				WHEN 'VL_OE_PL_COR_SC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_oe_pl_cor_sc_w;
				WHEN 'VL_OE_PL_CON_SC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_oe_pl_con_sc_w;
				WHEN 'VL_OE_PL_COR_CC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_oe_pl_cor_cc_w;
				WHEN 'VL_OD_PP_CON_SC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_od_pp_con_sc_w;
				WHEN 'VL_OD_PP_COR_SC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_od_pp_cor_sc_w;
				WHEN 'VL_OD_PP_COR_CC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_od_pp_cor_cc_w;
				WHEN 'VL_OD_PP_CON_CC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_od_pp_con_cc_w;
				WHEN 'VL_OE_PL_CON_CC' THEN
					vListaAcuidade[i].nr_valor	:=	vl_oe_pl_con_cc_w;
				WHEN 'IE_TIPO' THEN
					vListaAcuidade[i].ds_valor	:=	ie_tipo_w;
				ELSE
					null;
			END CASE;
		end if;
		END;
	END LOOP;
END IF;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_acuidade ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaAcuidade INOUT strRecTypeFormOft) FROM PUBLIC;

