SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_anamnese ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaAnamnese INOUT strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w			oft_anamnese.nr_sequencia%type;
dt_anamnese_w			oft_anamnese.dt_anamnese%type;
ds_anamnese_w			oft_anamnese.ds_anamnese%type;
ds_historia_ocular_w	oft_anamnese.ds_historia_ocular%type;
ds_medicamentos_w		oft_anamnese.ds_medicamentos%type;
ds_alergia_w			oft_anamnese.ds_alergia%type;
ds_justificativa_w	oft_anamnese.ds_justificativa%type;
cd_profissional_w		oft_anamnese.cd_profissional%TYPE;
dt_liberacao_w			timestamp;
nm_usuario_w			usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
ds_erro_w				varchar(4000);

anamnese_form CURSOR FOR
	SELECT	a.*
	from		oft_anamnese a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_anamnese;

anamnese_paciente CURSOR FOR
	SELECT	a.*
	from		oft_anamnese a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_anamnese;
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaAnamnese.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_anamnese IN anamnese_form LOOP
			begin
			dt_anamnese_w			:= c_anamnese.dt_anamnese;
			ds_anamnese_w			:=	c_anamnese.ds_anamnese;
			ds_historia_ocular_w	:=	c_anamnese.ds_historia_ocular;
			ds_medicamentos_w		:=	c_anamnese.ds_medicamentos;
			ds_alergia_w			:=	c_anamnese.ds_alergia;
			ds_justificativa_w	:=	c_anamnese.ds_justificativa;
			dt_liberacao_w			:=	c_anamnese.dt_liberacao;
			cd_profissional_w		:=	c_anamnese.cd_profissional;
			end;
		end loop;
	else
		FOR c_anamnese IN anamnese_paciente LOOP
			begin
			cd_profissional_w		:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_anamnese_w			:= clock_timestamp();
			ds_anamnese_w			:=	c_anamnese.ds_anamnese;
			ds_historia_ocular_w	:=	c_anamnese.ds_historia_ocular;
			ds_medicamentos_w		:=	c_anamnese.ds_medicamentos;
			ds_alergia_w			:=	c_anamnese.ds_alergia;
			ds_justificativa_w	:=	c_anamnese.ds_justificativa;
			end;
		end loop;
	end if;
	for i in 1..vListaAnamnese.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaAnamnese[i].ie_obter_resultado = 'S') then
			vListaAnamnese[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaAnamnese[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaAnamnese[i].ds_valor	:= cd_profissional_w;
				when 'DT_ANAMNESE' then
					vListaAnamnese[i].dt_valor	:=	dt_anamnese_w;
				when 'DS_ANAMNESE' then
					vListaAnamnese[i].ds_valor	:=	ds_anamnese_w;
				when 'DS_HISTORIA_OCULAR' then
					vListaAnamnese[i].ds_valor	:=	ds_historia_ocular_w;
				when 'DS_MEDICAMENTOS' then
					vListaAnamnese[i].ds_valor	:=	ds_medicamentos_w;
				when 'DS_ALERGIA' then
					vListaAnamnese[i].ds_valor	:=	ds_alergia_w;
				when 'DS_JUSTIFICATIVA' then
					vListaAnamnese[i].ds_valor	:=	ds_justificativa_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_anamnese ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaAnamnese INOUT strRecTypeFormOft) FROM PUBLIC;

