SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_angio_retino ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaAngioRetino INOUT strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w				oft_angio_retino.nr_sequencia%type;
dt_exame_w					oft_angio_retino.dt_registro%type;
ds_observacao_w			oft_angio_retino.ds_observacao%type;	
ds_angiografia_w			oft_angio_retino.ds_angiografia%type;
ds_retinografia_w			oft_angio_retino.ds_retinografia%type;
ds_oe_angiografia_w		oft_angio_retino.ds_oe_angiografia%type;
ds_oe_retinografia_w		oft_angio_retino.ds_oe_retinografia%type;
cd_profissional_w			oft_angio_retino.cd_profissional%TYPE;
ie_tipo_registro_w      oft_angio_retino.ie_tipo_registro%TYPE := 'A';
dt_liberacao_w				timestamp;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
ds_erro_w					varchar(4000);

angio_retino_form CURSOR FOR
	SELECT	a.*
	from		oft_angio_retino a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
   and		coalesce(a.ie_tipo_registro,'A') =	ie_tipo_registro_w
	order by dt_registro;
	
angio_retino_paciente CURSOR FOR
	SELECT	a.*
	from		oft_angio_retino a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
   and		coalesce(a.ie_tipo_registro,'A') =	ie_tipo_registro_w
	order by dt_registro;
	
	
								
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaAngioRetino.count > 0) then
   for i in 1..vListaAngioRetino.count loop
		begin
		if (vListaAngioRetino[i].nr_seq_visao = 86068) then
			ie_tipo_registro_w := 'A';
		elsif (vListaAngioRetino[i].nr_seq_visao = 101309) then
			ie_tipo_registro_w := 'R';
		end if;	
		end;
	end loop;

	if (ie_opcao_p = 'F') then
		FOR c_angio_retino IN angio_retino_form LOOP
			begin
			dt_exame_w					:= c_angio_retino.dt_registro;
			ds_observacao_w			:= c_angio_retino.ds_observacao;	
			ds_angiografia_w			:= c_angio_retino.ds_angiografia;
			ds_retinografia_w			:= c_angio_retino.ds_retinografia;
			ds_oe_angiografia_w		:= c_angio_retino.ds_oe_angiografia;
			ds_oe_retinografia_w		:= c_angio_retino.ds_oe_retinografia;
			dt_liberacao_w				:=	c_angio_retino.dt_liberacao;
			cd_profissional_w			:=	c_angio_retino.cd_profissional;
         ie_tipo_registro_w		:=	c_angio_retino.ie_tipo_registro;
			end;
		end loop;	
	else
		FOR c_angio_retino IN angio_retino_paciente LOOP
			begin
			ds_observacao_w			:= c_angio_retino.ds_observacao;	
			ds_angiografia_w			:= c_angio_retino.ds_angiografia;
			ds_retinografia_w			:= c_angio_retino.ds_retinografia;
			ds_oe_angiografia_w		:= c_angio_retino.ds_oe_angiografia;
			ds_oe_retinografia_w		:= c_angio_retino.ds_oe_retinografia;
         ie_tipo_registro_w		:=	c_angio_retino.ie_tipo_registro;
			cd_profissional_w			:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w					:= clock_timestamp();
			end;
		end loop;	
	end if;	
	
	for i in 1..vListaAngioRetino.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaAngioRetino[i].ie_obter_resultado = 'S') then
			vListaAngioRetino[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaAngioRetino[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaAngioRetino[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaAngioRetino[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaAngioRetino[i].ds_valor	:=	ds_observacao_w;
				when 'DS_ANGIOGRAFIA' then
					vlistaangioretino[i].ds_valor	:=	ds_angiografia_w;
				when 'DS_RETINOGRAFIA' then
					vListaAngioRetino[i].ds_valor	:=	ds_retinografia_w;
				when 'DS_OE_ANGIOGRAFIA' then
					vListaAngioRetino[i].ds_valor	:=	ds_oe_angiografia_w;
				when 'DS_OE_RETINOGRAFIA' then
					vListaAngioRetino[i].ds_valor	:=	ds_oe_retinografia_w;
				else
					null;	
			end case;	
		end if;	
	end;
	end loop;
end if;	

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_angio_retino ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaAngioRetino INOUT strRecTypeFormOft) FROM PUBLIC;

