SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_auto_refracao ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaAutoRefracao INOUT strRecTypeFormOft) AS $body$
DECLARE



ds_observacao_w			oft_auto_refracao.ds_observacao%type;
dt_exame_w					oft_auto_refracao.dt_exame%type;
ie_receita_dinamica_w	oft_auto_refracao.ie_receita_dinamica%type;
ie_receita_estatica_w	oft_auto_refracao.ie_receita_estatica%type;
qt_distancia_np_w			oft_auto_refracao.qt_distancia_np%type;
qt_reflexo_are_od_w		oft_auto_refracao.qt_reflexo_are_od%type;
qt_reflexo_are_oe_w		oft_auto_refracao.qt_reflexo_are_oe%type;
qt_reflexo_od_w			oft_auto_refracao.qt_reflexo_od%type;
qt_reflexo_oe_w			oft_auto_refracao.qt_reflexo_oe%type;
vl_od_ard_eixo_w			oft_auto_refracao.vl_od_ard_eixo%type;
vl_od_ard_esf_w			oft_auto_refracao.vl_od_ard_esf%type;
vl_od_are_eixo_w			oft_auto_refracao.vl_od_are_eixo%type;
vl_od_are_esf_w			oft_auto_refracao.vl_od_are_esf%type;
vl_oe_ard_eixo_w			oft_auto_refracao.vl_oe_ard_eixo%type;
vl_oe_ard_esf_w			oft_auto_refracao.vl_oe_ard_esf%type;
vl_oe_are_eixo_w			oft_auto_refracao.vl_oe_are_eixo%type;
vl_oe_are_esf_w			oft_auto_refracao.vl_oe_are_esf%type;
vl_oe_are_cil_w			oft_auto_refracao.vl_oe_are_cil%type;
vl_oe_ard_cil_w			oft_auto_refracao.vl_oe_ard_cil%type;
vl_od_are_cil_w			oft_auto_refracao.vl_od_are_cil%type;
vl_od_ard_cil_w			oft_auto_refracao.vl_od_ard_cil%type;
cd_profissional_w			oft_auto_refracao.cd_profissional%TYPE;
dt_liberacao_w				timestamp;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
ds_erro_w					varchar(4000);

auto_refracao_form CURSOR FOR
	SELECT	a.*
	from		oft_auto_refracao a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_exame;

auto_refracao_paciente CURSOR FOR
	SELECT	a.*
	from		oft_auto_refracao a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_exame;


BEGIN
begin
if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaAutoRefracao.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_auto_refracao IN auto_refracao_form LOOP
			begin
			ds_observacao_w			:=	c_auto_refracao.ds_observacao;
			dt_exame_w					:=	c_auto_refracao.dt_exame;
			ie_receita_dinamica_w	:=	c_auto_refracao.ie_receita_dinamica;
			ie_receita_estatica_w	:=	c_auto_refracao.ie_receita_estatica;
			qt_distancia_np_w			:=	c_auto_refracao.qt_distancia_np;
			qt_reflexo_are_od_w		:=	c_auto_refracao.qt_reflexo_are_od;
			qt_reflexo_are_oe_w		:=	c_auto_refracao.qt_reflexo_are_oe;
			qt_reflexo_od_w			:=	c_auto_refracao.qt_reflexo_od;
			qt_reflexo_oe_w			:=	c_auto_refracao.qt_reflexo_oe;
			vl_od_ard_eixo_w			:=	c_auto_refracao.vl_od_ard_eixo;
			vl_od_ard_esf_w			:=	c_auto_refracao.vl_od_ard_esf;
			vl_od_are_eixo_w			:=	c_auto_refracao.vl_od_are_eixo;
			vl_od_are_esf_w			:=	c_auto_refracao.vl_od_are_esf;
			vl_oe_ard_eixo_w			:=	c_auto_refracao.vl_oe_ard_eixo;
			vl_oe_ard_esf_w			:=	c_auto_refracao.vl_oe_ard_esf;
			vl_oe_are_eixo_w			:=	c_auto_refracao.vl_oe_are_eixo;
			vl_oe_are_esf_w			:=	c_auto_refracao.vl_oe_are_esf;
			dt_liberacao_w				:=	c_auto_refracao.dt_liberacao;
			vl_oe_are_cil_w			:=	c_auto_refracao.vl_oe_are_cil;
			vl_oe_ard_cil_w			:=	c_auto_refracao.vl_oe_ard_cil;
			vl_od_are_cil_w			:=	c_auto_refracao.vl_od_are_cil;
			vl_od_ard_cil_w			:=	c_auto_refracao.vl_od_ard_cil;
			cd_profissional_w			:=	c_auto_refracao.cd_profissional;
			end;
		end loop;
	else
		FOR c_auto_refracao IN auto_refracao_paciente LOOP
			begin
			ds_observacao_w			:=	c_auto_refracao.ds_observacao;
			cd_profissional_w			:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w					:= clock_timestamp();
			ie_receita_dinamica_w	:=	c_auto_refracao.ie_receita_dinamica;
			ie_receita_estatica_w	:=	c_auto_refracao.ie_receita_estatica;
			qt_distancia_np_w			:=	c_auto_refracao.qt_distancia_np;
			qt_reflexo_are_od_w		:=	c_auto_refracao.qt_reflexo_are_od;
			qt_reflexo_are_oe_w		:=	c_auto_refracao.qt_reflexo_are_oe;
			qt_reflexo_od_w			:=	c_auto_refracao.qt_reflexo_od;
			qt_reflexo_oe_w			:=	c_auto_refracao.qt_reflexo_oe;
			vl_od_ard_eixo_w			:=	c_auto_refracao.vl_od_ard_eixo;
			vl_od_ard_esf_w			:=	c_auto_refracao.vl_od_ard_esf;
			vl_od_are_eixo_w			:=	c_auto_refracao.vl_od_are_eixo;
			vl_od_are_esf_w			:=	c_auto_refracao.vl_od_are_esf;
			vl_oe_ard_eixo_w			:=	c_auto_refracao.vl_oe_ard_eixo;
			vl_oe_ard_esf_w			:=	c_auto_refracao.vl_oe_ard_esf;
			vl_oe_are_eixo_w			:=	c_auto_refracao.vl_oe_are_eixo;
			vl_oe_are_esf_w			:=	c_auto_refracao.vl_oe_are_esf;
			vl_oe_are_cil_w			:=	c_auto_refracao.vl_oe_are_cil;
			vl_oe_ard_cil_w			:=	c_auto_refracao.vl_oe_ard_cil;
			vl_od_are_cil_w			:=	c_auto_refracao.vl_od_are_cil;
			vl_od_ard_cil_w			:=	c_auto_refracao.vl_od_ard_cil;
			end;
		end loop;
	end if;

	for i in 1..vListaAutoRefracao.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaAutoRefracao[i].ie_obter_resultado = 'S') then
			vListaAutoRefracao[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaAutoRefracao[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaAutoRefracao[i].ds_valor	:= cd_profissional_w;
				when 'DT_EXAME' then
					vListaAutoRefracao[i].dt_valor	:=	dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaAutoRefracao[i].ds_valor	:=	DS_OBSERVACAO_w;
				when 'IE_RECEITA_DINAMICA' then
					vListaAutoRefracao[i].ds_valor	:=	IE_RECEITA_DINAMICA_w;
				when 'IE_RECEITA_ESTATICA' then
					vListaAutoRefracao[i].ds_valor	:=	IE_RECEITA_ESTATICA_w;
				when 'QT_DISTANCIA_NP' then
					vListaAutoRefracao[i].nr_valor	:=	QT_DISTANCIA_NP_w;
				when 'QT_REFLEXO_ARE_OD' then
					vListaAutoRefracao[i].nr_valor	:=	QT_REFLEXO_ARE_OD_w;
				when 'QT_REFLEXO_ARE_OE' then
					vListaAutoRefracao[i].nr_valor	:=	QT_REFLEXO_ARE_OE_w;
				when 'QT_REFLEXO_OD' then
					vListaAutoRefracao[i].nr_valor	:=	QT_REFLEXO_OD_w;
				when 'QT_REFLEXO_OE' then
					vListaAutoRefracao[i].nr_valor	:=	QT_REFLEXO_OE_w;
				when 'VL_OD_ARD_EIXO' then
					vListaAutoRefracao[i].nr_valor	:=	VL_OD_ARD_EIXO_w;
				when 'VL_OD_ARD_ESF' then
					vListaAutoRefracao[i].nr_valor	:=	VL_OD_ARD_ESF_w;
				when 'VL_OD_ARE_EIXO' then
					vListaAutoRefracao[i].nr_valor	:=	VL_OD_ARE_EIXO_w;
				when 'VL_OD_ARE_ESF' then
					vListaAutoRefracao[i].nr_valor	:=	VL_OD_ARE_ESF_w;
				when 'VL_OE_ARD_EIXO' then
					vListaAutoRefracao[i].nr_valor	:=	VL_OE_ARD_EIXO_w;
				when 'VL_OE_ARD_ESF' then
					vListaAutoRefracao[i].nr_valor	:=	VL_OE_ARD_ESF_w;
				when 'VL_OE_ARE_EIXO' then
					vListaAutoRefracao[i].nr_valor	:=	VL_OE_ARE_EIXO_w;
				when 'VL_OE_ARE_ESF' then
					vListaAutoRefracao[i].nr_valor	:=	VL_OE_ARE_ESF_w;
				when 'VL_OE_ARE_CIL' then
					vListaAutoRefracao[i].nr_valor	:=	vl_oe_are_cil_w;
				when 'VL_OE_ARD_CIL' then
					vListaAutoRefracao[i].nr_valor	:=	vl_oe_ard_cil_w;
				when 'VL_OD_ARE_CIL' then
					vListaAutoRefracao[i].nr_valor	:=	vl_od_are_cil_w;
				when 'VL_OD_ARD_CIL' then
					vListaAutoRefracao[i].nr_valor	:=	vl_od_ard_cil_w;
				else
					null;
				end case;
		end if;
		end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_auto_refracao ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaAutoRefracao INOUT strRecTypeFormOft) FROM PUBLIC;

