SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_biomicroscopia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaBiomicroscopia INOUT strRecTypeFormOft) AS $body$
DECLARE


ds_observacao_w					oft_biomicroscopia.ds_observacao%type;
dt_exame_w							oft_biomicroscopia.dt_exame%type;
ds_biomicroscopia_od_w			oft_biomicroscopia.ds_biomicroscopia_od%type;
ds_biomicroscopia_pos_oe_w		oft_biomicroscopia.ds_biomicroscopia_pos_oe%type;
ds_biomicroscopia_oe_w			oft_biomicroscopia.ds_biomicroscopia_oe%type;
ie_normal_oe_w						oft_biomicroscopia.ie_normal_oe%type;
ds_biomicroscopia_pos_od_w		oft_biomicroscopia.ds_biomicroscopia_pos_od%type;
ie_normal_od_w						oft_biomicroscopia.ie_normal_od%type;
ie_segmento_w						oft_biomicroscopia.ie_segmento%type;
ds_mapeamento_od_w				oft_biomicroscopia.ds_mapeamento_od%type;
ds_mapeamento_oe_w				oft_biomicroscopia.ds_mapeamento_oe%type;
cd_profissional_w					oft_biomicroscopia.cd_profissional%TYPE;
dt_liberacao_w						timestamp;
cd_estabelecimento_w				estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w						usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w							varchar(4000);

biomicroscopia_form CURSOR FOR
	SELECT	a.*
	from		oft_biomicroscopia a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_exame;

biomicroscopia_paciente CURSOR FOR
	SELECT	a.*
	from		oft_biomicroscopia a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_exame;

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaBiomicroscopia.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_biomicroscopia IN biomicroscopia_form LOOP
			begin
			ds_observacao_w					:= c_biomicroscopia.ds_observacao;
			dt_exame_w							:= c_biomicroscopia.dt_exame;
			ds_biomicroscopia_od_w			:= c_biomicroscopia.ds_biomicroscopia_od;
			ds_biomicroscopia_pos_oe_w		:= c_biomicroscopia.ds_biomicroscopia_pos_oe;
			ds_biomicroscopia_oe_w			:= c_biomicroscopia.ds_biomicroscopia_oe;
			ie_normal_oe_w						:= c_biomicroscopia.ie_normal_oe;
			ds_biomicroscopia_pos_od_w		:= c_biomicroscopia.ds_biomicroscopia_pos_od;
			ie_normal_od_w						:= c_biomicroscopia.ie_normal_od;
			ie_segmento_w						:= c_biomicroscopia.ie_segmento;
			ds_mapeamento_od_w				:= c_biomicroscopia.ds_mapeamento_od;
			ds_mapeamento_oe_w				:= c_biomicroscopia.ds_mapeamento_oe;
			dt_liberacao_w						:=	c_biomicroscopia.dt_liberacao;
			cd_profissional_w					:=	c_biomicroscopia.cd_profissional;
			end;
		end loop;
	else
		FOR c_biomicroscopia IN biomicroscopia_paciente LOOP
			begin
			ds_observacao_w					:= c_biomicroscopia.ds_observacao;
			ds_biomicroscopia_od_w			:= c_biomicroscopia.ds_biomicroscopia_od;
			ds_biomicroscopia_pos_oe_w		:= c_biomicroscopia.ds_biomicroscopia_pos_oe;
			ds_biomicroscopia_oe_w			:= c_biomicroscopia.ds_biomicroscopia_oe;
			ie_normal_oe_w						:= c_biomicroscopia.ie_normal_oe;
			ds_biomicroscopia_pos_od_w		:= c_biomicroscopia.ds_biomicroscopia_pos_od;
			ie_normal_od_w						:= c_biomicroscopia.ie_normal_od;
			ie_segmento_w						:= c_biomicroscopia.ie_segmento;
			ds_mapeamento_od_w				:= c_biomicroscopia.ds_mapeamento_od;
			ds_mapeamento_oe_w				:= c_biomicroscopia.ds_mapeamento_oe;
			cd_profissional_w					:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w							:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaBiomicroscopia.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaBiomicroscopia[i].ie_obter_resultado = 'S') then
			vListaBiomicroscopia[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaBiomicroscopia[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaBiomicroscopia[i].ds_valor	:= cd_profissional_w;
				when 'DT_EXAME' then
					vListaBiomicroscopia[i].dt_valor	:=	dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaBiomicroscopia[i].ds_valor	:=	ds_observacao_w;
				when 'DS_BIOMICROSCOPIA_OD' then
					vListaBiomicroscopia[i].ds_valor	:=	ds_biomicroscopia_od_w;
				when 'DS_BIOMICROSCOPIA_OE' then
					vListaBiomicroscopia[i].ds_valor	:=	ds_biomicroscopia_oe_w;
				when 'DS_BIOMICROSCOPIA_POS_OD' then
					vListaBiomicroscopia[i].ds_valor	:=	ds_biomicroscopia_pos_od_w;
				when 'DS_BIOMICROSCOPIA_POS_OE' then
					vListaBiomicroscopia[i].ds_valor	:=	ds_biomicroscopia_pos_oe_w;
				when 'DS_MAPEAMENTO_OD' then
					vListaBiomicroscopia[i].ds_valor	:=	ds_mapeamento_od_w;
				when 'DS_MAPEAMENTO_OE' then
					vListaBiomicroscopia[i].ds_valor	:=	ds_mapeamento_oe_w;
				when 'IE_NORMAL_OD' then
					vListaBiomicroscopia[i].ds_valor	:=	ie_normal_od_w;
				when 'IE_NORMAL_OE' then
					vListaBiomicroscopia[i].ds_valor	:=	ie_normal_oe_w;
				when 'IE_SEGMENTO' then
					vListaBiomicroscopia[i].ds_valor	:=	ie_segmento_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_biomicroscopia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaBiomicroscopia INOUT strRecTypeFormOft) FROM PUBLIC;

