SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_capsulotomia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaCapsulotomia INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w						oft_capsulotomia.dt_registro%type;
ds_observacao_w				oft_capsulotomia.ds_observacao%type;
ie_olho_w						oft_capsulotomia.ie_olho%type;
nr_potencia_w					oft_capsulotomia.nr_potencia%type;
qt_disparos_w					oft_capsulotomia.qt_disparos%type;
qt_energia_utilizada_w		oft_capsulotomia.qt_energia_utilizada%type;
qt_tamanho_w					oft_capsulotomia.qt_tamanho%type;
cd_profissional_w				oft_capsulotomia.cd_profissional%TYPE;
dt_liberacao_w					timestamp;
cd_estabelecimento_w			estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w					usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w						varchar(4000);

capsulotomia_form CURSOR FOR
	SELECT	a.*
	from		oft_capsulotomia a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_registro;

capsulotomia_paciente CURSOR FOR
	SELECT	a.*
	from		oft_capsulotomia a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_registro;


BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaCapsulotomia.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_capsulotomia IN capsulotomia_form LOOP
			begin
			dt_exame_w						:= c_capsulotomia.dt_registro;
			ds_observacao_w				:= c_capsulotomia.ds_observacao;
			ie_olho_w						:= c_capsulotomia.ie_olho;
			nr_potencia_w					:= c_capsulotomia.nr_potencia;
			qt_disparos_w					:= c_capsulotomia.qt_disparos;
			qt_energia_utilizada_w		:= c_capsulotomia.qt_energia_utilizada;
			qt_tamanho_w					:= c_capsulotomia.qt_tamanho;
			dt_liberacao_w					:=	c_capsulotomia.dt_liberacao;
			cd_profissional_w				:=	c_capsulotomia.cd_profissional;
			end;
		end loop;
	else
		FOR c_capsulotomia IN capsulotomia_paciente LOOP
			begin
			ds_observacao_w				:= c_capsulotomia.ds_observacao;
			ie_olho_w						:= c_capsulotomia.ie_olho;
			nr_potencia_w					:= c_capsulotomia.nr_potencia;
			qt_disparos_w					:= c_capsulotomia.qt_disparos;
			qt_energia_utilizada_w		:= c_capsulotomia.qt_energia_utilizada;
			qt_tamanho_w					:= c_capsulotomia.qt_tamanho;
			cd_profissional_w				:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w						:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaCapsulotomia.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaCapsulotomia[i].ie_obter_resultado = 'S') then
			vListaCapsulotomia[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaCapsulotomia[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaCapsulotomia[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaCapsulotomia[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaCapsulotomia[i].ds_valor	:=	ds_observacao_w;
				when 'IE_OLHO' then
					vListaCapsulotomia[i].ds_valor	:=	ie_olho_w;
				when 'NR_POTENCIA' then
					vListaCapsulotomia[i].nr_valor	:=	nr_potencia_w;
				when 'QT_DISPAROS' then
					vListaCapsulotomia[i].nr_valor	:=	qt_disparos_w;
				when 'QT_ENERGIA_UTILIZADA' then
					vListaCapsulotomia[i].nr_valor	:=	qt_energia_utilizada_w;
				when 'QT_TAMANHO' then
					vListaCapsulotomia[i].nr_valor	:=	qt_tamanho_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_capsulotomia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaCapsulotomia INOUT strRecTypeFormOft) FROM PUBLIC;

