SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_conduta ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaConduta INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w							oft_conduta.dt_registro%type;
ds_conduta_w						oft_conduta.ds_conduta%type;
qt_periodo_w 						oft_conduta.qt_periodo%type;
ie_periodo_w						oft_conduta.ie_periodo%type;
ds_inf_pre_cirurgica_w			oft_conduta.ds_inf_pre_cirurgica%type;
cd_profissional_w					oft_conduta.cd_profissional%TYPE;
ds_observacao_w					oft_conduta.ds_observacao%TYPE;
cd_destino_pac_w oft_conduta.cd_destino_pac%type;
dt_liberacao_w						timestamp;
cd_estabelecimento_w				estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w						usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w							varchar(4000);

conduta_form CURSOR FOR
	SELECT	a.*
	from		oft_conduta a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_registro;
	
conduta_paciente CURSOR FOR
	SELECT	a.*
	from		oft_conduta a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_registro;

											
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaConduta.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_conduta IN conduta_form LOOP
			begin
			dt_exame_w					:=	c_conduta.dt_registro;
			ds_conduta_w				:=	c_conduta.ds_conduta;
			qt_periodo_w 				:=	c_conduta.qt_periodo;
			ie_periodo_w				:=	c_conduta.ie_periodo;
			ds_inf_pre_cirurgica_w	:=	c_conduta.ds_inf_pre_cirurgica;
			dt_liberacao_w				:=	c_conduta.dt_liberacao;
			cd_profissional_w			:=	c_conduta.cd_profissional;
			ds_observacao_w			:=	c_conduta.ds_observacao;
			cd_destino_pac_w            := c_conduta.cd_destino_pac;
			end;
		end loop;	
	else
		FOR c_conduta IN conduta_paciente LOOP
			begin
			ds_conduta_w				:=	c_conduta.ds_conduta;
			qt_periodo_w 				:=	c_conduta.qt_periodo;
			ie_periodo_w				:=	c_conduta.ie_periodo;
			ds_inf_pre_cirurgica_w	:=	c_conduta.ds_inf_pre_cirurgica;
			cd_profissional_w			:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w					:= clock_timestamp();
			ds_observacao_w			:=	c_conduta.ds_observacao;
			cd_destino_pac_w       := c_conduta.cd_destino_pac;
			end;
		end loop;	
	end if;

	for i in 1..vListaConduta.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaConduta[i].ie_obter_resultado = 'S') then
			vListaConduta[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaConduta[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaConduta[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaConduta[i].dt_valor	:=	dt_exame_w;
				when 'DS_CONDUTA' then
					vListaConduta[i].ds_valor	:= ds_conduta_w;
				when 'QT_PERIODO' then
					vListaConduta[i].nr_valor	:=	qt_periodo_w;
				when 'IE_PERIODO' then
					vListaConduta[i].ds_valor	:=	ie_periodo_w;
				when 'DS_INF_PRE_CIRURGICA' then
					vListaConduta[i].ds_valor	:=	ds_inf_pre_cirurgica_w;
				when 'DS_OBSERVACAO' then
					vListaConduta[i].ds_valor	:=	ds_observacao_w;
				when 'CD_DESTINO_PAC' then
              		vListaConduta[i].nr_valor := cd_destino_pac_w;
				else
					null;	
			end case;	
		end if;	
		end;
	end loop;
end if;	

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_conduta ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaConduta INOUT strRecTypeFormOft) FROM PUBLIC;

