SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_curva_tencional ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaCurvaTencional INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w					oft_curva_tencional.dt_registro%type;
ds_observacao_w			oft_curva_tencional.ds_observacao%type;
vl_od_ct_w					oft_curva_tencional.vl_od_ct%type;
vl_oe_ct_w					oft_curva_tencional.vl_oe_ct%type;
cd_equipamento_w			oft_curva_tencional.cd_equipamento%type;
cd_profissional_w			oft_curva_tencional.cd_profissional%TYPE;
dt_liberacao_w				timestamp;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w					varchar(4000);

curva_tencional_form CURSOR FOR
	SELECT	a.*
	from		oft_curva_tencional a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_registro;

curva_tencional_paciente CURSOR FOR
	SELECT	a.*
	from		oft_curva_tencional a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_registro;
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaCurvaTencional.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_curva_tencional IN curva_tencional_form LOOP
			begin
			dt_exame_w			:=	c_curva_tencional.dt_registro;
			ds_observacao_w	:=	c_curva_tencional.ds_observacao;
			vl_od_ct_w			:=	c_curva_tencional.vl_od_ct;
			vl_oe_ct_w			:=	c_curva_tencional.vl_oe_ct;
			cd_equipamento_w	:=	c_curva_tencional.cd_equipamento;
			dt_liberacao_w		:=	c_curva_tencional.dt_liberacao;
			cd_profissional_w	:=	c_curva_tencional.cd_profissional;
			end;
		end loop;
	else
		FOR c_curva_tencional IN curva_tencional_paciente LOOP
			begin
			ds_observacao_w	:=	c_curva_tencional.ds_observacao;
			vl_od_ct_w			:=	c_curva_tencional.vl_od_ct;
			vl_oe_ct_w			:=	c_curva_tencional.vl_oe_ct;
			cd_equipamento_w	:=	c_curva_tencional.cd_equipamento;
			cd_profissional_w	:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w			:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaCurvaTencional.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaCurvaTencional[i].ie_obter_resultado = 'S') then
			vListaCurvaTencional[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaCurvaTencional[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaCurvaTencional[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaCurvaTencional[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaCurvaTencional[i].ds_valor	:=	ds_observacao_w;
				when 'VL_OD_CT' then
					vListaCurvaTencional[i].nr_valor	:=	vl_od_ct_w;
				when 'VL_OE_CT' then
					vListaCurvaTencional[i].nr_valor	:=	vl_oe_ct_w;
				when 'CD_EQUIPAMENTO' then
					vListaCurvaTencional[i].nr_valor	:=	cd_equipamento_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_curva_tencional ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaCurvaTencional INOUT strRecTypeFormOft) FROM PUBLIC;

