SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_diagnostico ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaDiagnostico INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w						oft_diagnostico.dt_registro%type;
nr_seq_diag_w					oft_diagnostico.nr_seq_diag%type;	
cd_doenca_cid_w				oft_diagnostico.cd_doenca_cid%type;
ds_diagnostico_w				oft_diagnostico.ds_diagnostico%type;
ds_observacao_w				oft_diagnostico.ds_observacao%type;
ie_olho_w						oft_diagnostico.ie_olho%type;
nr_seq_grau_parentesco_w	oft_diagnostico.nr_seq_grau_parentesco%type;
ie_paciente_w					oft_diagnostico.ie_paciente%type;
dt_inicio_w						oft_diagnostico.dt_inicio%type;
dt_fim_w							oft_diagnostico.dt_fim%type;
cd_profissional_w				oft_diagnostico.cd_profissional%TYPE;
dt_liberacao_w					timestamp;
nm_usuario_w					usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w			estabelecimento.cd_estabelecimento%type 	:= wheb_usuario_pck.get_cd_estabelecimento;
ds_erro_w						varchar(4000);
nr_seq_campo_formulario_w			oft_diagnostico.nr_seq_campo_formulario%type;

diagnostico_form CURSOR FOR
	SELECT	a.*
	from		oft_diagnostico a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.ie_paciente				=	ie_paciente_w
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	and		coalesce(a.nr_seq_campo_formulario, 1) 	=	nr_seq_campo_formulario_w
	order by dt_registro;
	
diagnostico_paciente CURSOR FOR
	SELECT	a.*
	from		oft_diagnostico a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		a.ie_paciente			=	ie_paciente_w
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	and		coalesce(a.nr_seq_campo_formulario, 1) 	=	nr_seq_campo_formulario_w
	order by dt_registro;
	
	
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaDiagnostico.count > 0) then
	nr_seq_campo_formulario_w := vListaDiagnostico[vListaDiagnostico.last].nr_aux_1;
	for i in 1..vListaDiagnostico.count loop
		begin
		if (vListaDiagnostico[i].nr_seq_visao = 86093) then
			ie_paciente_w := 'S';
		else
			ie_paciente_w := 'N';
		end if;	
		end;
	end loop;	
	if (ie_opcao_p = 'F') then
		FOR c_diagnostico IN diagnostico_form LOOP
			begin
			dt_exame_w						:= c_diagnostico.dt_registro;
			nr_seq_diag_w					:= c_diagnostico.nr_seq_diag;	
			cd_doenca_cid_w				:= c_diagnostico.cd_doenca_cid;
			ds_diagnostico_w				:= c_diagnostico.ds_diagnostico;
			ds_observacao_w				:= c_diagnostico.ds_observacao;
			ie_olho_w						:= c_diagnostico.ie_olho;
			nr_seq_grau_parentesco_w	:= c_diagnostico.nr_seq_grau_parentesco;
			dt_inicio_w						:= c_diagnostico.dt_inicio;
			dt_fim_w							:= c_diagnostico.dt_fim;
			cd_profissional_w				:=	c_diagnostico.cd_profissional;
			dt_liberacao_w					:= c_diagnostico.dt_liberacao;
			end;
		end loop;	
	else
		FOR c_diagnostico IN diagnostico_paciente LOOP
			begin
			nr_seq_diag_w					:= c_diagnostico.nr_seq_diag;	
			cd_doenca_cid_w				:= c_diagnostico.cd_doenca_cid;
			ds_diagnostico_w				:= c_diagnostico.ds_diagnostico;
			ds_observacao_w				:= c_diagnostico.ds_observacao;
			ie_olho_w						:= c_diagnostico.ie_olho;
			nr_seq_grau_parentesco_w	:= c_diagnostico.nr_seq_grau_parentesco;
			dt_inicio_w						:= c_diagnostico.dt_inicio;
			dt_fim_w							:= c_diagnostico.dt_fim;
			cd_profissional_w				:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w						:= clock_timestamp();
			end;
		end loop;	
	end if;

	for i in 1..vListaDiagnostico.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaDiagnostico[i].ie_obter_resultado = 'S') then
			vListaDiagnostico[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaDiagnostico[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaDiagnostico[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaDiagnostico[i].dt_valor	:= dt_exame_w;
				when 'NR_SEQ_DIAG' then
					vListaDiagnostico[i].nr_valor := nr_seq_diag_w;
				when 'CD_DOENCA_CID' then
					vListaDiagnostico[i].ds_valor := cd_doenca_cid_w;
				when 'DS_DIAGNOSTICO' then
					vListaDiagnostico[i].ds_valor := ds_diagnostico_w;
				when 'DS_OBSERVACAO' then
					vListaDiagnostico[i].ds_valor := ds_observacao_w;
				when 'IE_OLHO' then
					vListaDiagnostico[i].ds_valor := ie_olho_w;
				when 'NR_SEQ_GRAU_PARENTESCO' then
					vListaDiagnostico[i].nr_valor := nr_seq_grau_parentesco_w;
				when 'IE_PACIENTE' then
					vListaDiagnostico[i].ds_valor := ie_paciente_w;
				when 'DT_INICIO' then
					vListaDiagnostico[i].dt_valor	:= dt_inicio_w;
				when 'DT_FIM' then
					vListaDiagnostico[i].dt_valor	:= dt_fim_w;
				else
					null;	
			end case;	
		end if;	
	end;
	end loop;
end if;	

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_diagnostico ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaDiagnostico INOUT strRecTypeFormOft) FROM PUBLIC;

