SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_dnp ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaDnp INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w					oft_dnp.dt_exame%type;
ds_observacao_w			oft_dnp.ds_observacao%type;
vl_od_ard_dnp_w			oft_dnp.vl_od_ard_dnp%type;
vl_ard_dp_w					oft_dnp.vl_ard_dp%type;
vl_oe_ard_dnp_w			oft_dnp.vl_oe_ard_dnp%type;
vl_od_pp_ard_dnp_w		oft_dnp.vl_od_pp_ard_dnp%type;
vl_pp_ard_dp_w				oft_dnp.vl_pp_ard_dp%type;
vl_oe_pp_ard_dnp_w		oft_dnp.vl_oe_pp_ard_dnp%type;
cd_profissional_w			oft_dnp.cd_profissional%TYPE;
dt_liberacao_w				timestamp;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w					varchar(4000);

dnp_form CURSOR FOR
	SELECT	a.*
	from		oft_dnp a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_exame;

dnp_paciente CURSOR FOR
	SELECT	a.*
	from		oft_dnp a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_exame;

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaDnp.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_dnp IN dnp_form LOOP
			begin
			dt_exame_w				:=	c_dnp.dt_exame;
			ds_observacao_w		:=	c_dnp.ds_observacao;
			vl_od_ard_dnp_w		:=	c_dnp.vl_od_ard_dnp;
			vl_ard_dp_w				:=	c_dnp.vl_ard_dp;
			vl_oe_ard_dnp_w		:=	c_dnp.vl_oe_ard_dnp;
			vl_od_pp_ard_dnp_w	:=	c_dnp.vl_od_pp_ard_dnp;
			vl_pp_ard_dp_w			:=	c_dnp.vl_pp_ard_dp;
			vl_oe_pp_ard_dnp_w	:=	c_dnp.vl_oe_pp_ard_dnp;
			dt_liberacao_w			:=	c_dnp.dt_liberacao;
			cd_profissional_w		:=	c_dnp.cd_profissional;
			end;
		end loop;
	else
		FOR c_dnp IN dnp_paciente LOOP
			begin
			ds_observacao_w		:=	c_dnp.ds_observacao;
			vl_od_ard_dnp_w		:=	c_dnp.vl_od_ard_dnp;
			vl_ard_dp_w				:=	c_dnp.vl_ard_dp;
			vl_oe_ard_dnp_w		:=	c_dnp.vl_oe_ard_dnp;
			vl_od_pp_ard_dnp_w	:=	c_dnp.vl_od_pp_ard_dnp;
			vl_pp_ard_dp_w			:=	c_dnp.vl_pp_ard_dp;
			vl_oe_pp_ard_dnp_w	:=	c_dnp.vl_oe_pp_ard_dnp;
			cd_profissional_w		:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w				:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaDnp.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaDnp[i].ie_obter_resultado = 'S') then
			vListaDnp[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaDnp[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaDnp[i].ds_valor	:= cd_profissional_w;
				when 'DT_EXAME' then
					vListaDnp[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaDnp[i].ds_valor	:=	ds_observacao_w;
				when 'VL_OD_ARD_DNP' then
					vListaDnp[i].nr_valor	:=	vl_od_ard_dnp_w;
				when 'VL_ARD_DP' then
					vListaDnp[i].nr_valor	:=	vl_ard_dp_w;
				when 'VL_OE_ARD_DNP' then
					vListaDnp[i].nr_valor	:=	vl_oe_ard_dnp_w;
				when 'VL_OD_PP_ARD_DNP' then
					vListaDnp[i].nr_valor	:=	vl_od_pp_ard_dnp_w;
				when 'VL_PP_ARD_DP' then
					vListaDnp[i].nr_valor	:=	vl_pp_ard_dp_w;
				when 'VL_OE_PP_ARD_DNP' then
					vListaDnp[i].nr_valor	:=	vl_oe_pp_ard_dnp_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
