CREATE OR REPLACE PROCEDURE oft_obter_fotocoagulacao ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaFotocoagulacao INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w						oft_fotocoagulacao_laser.dt_registro%type;
ds_observacao_w				oft_fotocoagulacao_laser.ds_observacao%type;
ie_olho_w						oft_fotocoagulacao_laser.ie_olho%type;
ie_laser_w						oft_fotocoagulacao_laser.ie_laser%type;
nr_disparo_posterior_w		oft_fotocoagulacao_laser.nr_disparo_posterior%type;
nr_disparo_superior_w		oft_fotocoagulacao_laser.nr_disparo_superior%type;
nr_disparo_inferior_w		oft_fotocoagulacao_laser.nr_disparo_inferior%type;
nr_disparo_nasal_w			oft_fotocoagulacao_laser.nr_disparo_nasal%type;
nr_disparo_temporal_w		oft_fotocoagulacao_laser.nr_disparo_temporal%type;
nr_disparo_inf_nasal_w		oft_fotocoagulacao_laser.nr_disparo_inf_nasal%type;
nr_disparo_sup_nasal_w		oft_fotocoagulacao_laser.nr_disparo_sup_nasal%type;
nr_disparo_inf_temporal_w	oft_fotocoagulacao_laser.nr_disparo_inf_temporal%type;
nr_disparo_sup_temporal_w	oft_fotocoagulacao_laser.nr_disparo_sup_temporal%type;
nr_disparo_macula_w			oft_fotocoagulacao_laser.nr_disparo_macula%type;
nr_mira_w						oft_fotocoagulacao_laser.nr_mira%type;
qt_intervalo_w					oft_fotocoagulacao_laser.qt_intervalo%type;
qt_potencia_posterior_w		oft_fotocoagulacao_laser.qt_potencia_posterior%type;
qt_potencia_superior_w		oft_fotocoagulacao_laser.qt_potencia_superior%type;
qt_potencia_inferior_w		oft_fotocoagulacao_laser.qt_potencia_inferior%type;
qt_potencia_nasal_w			oft_fotocoagulacao_laser.qt_potencia_nasal%type;
qt_potencia_temporal_w		oft_fotocoagulacao_laser.qt_potencia_temporal%type;
qt_potencia_inf_nasal_w		oft_fotocoagulacao_laser.qt_potencia_inf_nasal%type;
qt_potencia_sup_nasal_w		oft_fotocoagulacao_laser.qt_potencia_sup_nasal%type;
qt_potencia_inf_temporal_w	oft_fotocoagulacao_laser.qt_potencia_inf_temporal%type;
qt_potencia_sup_temporal_w	oft_fotocoagulacao_laser.qt_potencia_sup_temporal%type;
qt_potencia_macula_w			oft_fotocoagulacao_laser.qt_potencia_macula%type;
cd_profissional_w				oft_fotocoagulacao_laser.cd_profissional%TYPE;
dt_liberacao_w					timestamp;
cd_estabelecimento_w			estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w					usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w						varchar(4000);

fotocoagulacao_laser_form CURSOR FOR
	SELECT	a.*
	from		oft_fotocoagulacao_laser a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_registro;

fotocoagulacao_laser_paciente CURSOR FOR
	SELECT	a.*
	from		oft_fotocoagulacao_laser a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_registro;
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaFotocoagulacao.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_fotocoagulacao_laser IN fotocoagulacao_laser_form LOOP
			begin
			dt_exame_w						:= c_fotocoagulacao_laser.dt_registro;
			ds_observacao_w				:= c_fotocoagulacao_laser.ds_observacao;
			ie_olho_w						:= c_fotocoagulacao_laser.ie_olho;
			ie_laser_w						:= c_fotocoagulacao_laser.ie_laser;
			nr_disparo_posterior_w		:= c_fotocoagulacao_laser.nr_disparo_posterior;
			nr_disparo_superior_w		:= c_fotocoagulacao_laser.nr_disparo_superior;
			nr_disparo_inferior_w		:= c_fotocoagulacao_laser.nr_disparo_inferior;
			nr_disparo_nasal_w			:= c_fotocoagulacao_laser.nr_disparo_nasal;
			nr_disparo_temporal_w		:= c_fotocoagulacao_laser.nr_disparo_temporal;
			nr_disparo_inf_nasal_w		:= c_fotocoagulacao_laser.nr_disparo_inf_nasal;
			nr_disparo_sup_nasal_w		:= c_fotocoagulacao_laser.nr_disparo_sup_nasal;
			nr_disparo_inf_temporal_w	:= c_fotocoagulacao_laser.nr_disparo_inf_temporal;
			nr_disparo_sup_temporal_w	:= c_fotocoagulacao_laser.nr_disparo_sup_temporal;
			nr_disparo_macula_w			:= c_fotocoagulacao_laser.nr_disparo_macula;
			nr_mira_w						:= c_fotocoagulacao_laser.nr_mira;
			qt_intervalo_w					:= c_fotocoagulacao_laser.qt_intervalo;
			qt_potencia_posterior_w		:= c_fotocoagulacao_laser.qt_potencia_posterior;
			qt_potencia_superior_w		:= c_fotocoagulacao_laser.qt_potencia_superior;
			qt_potencia_inferior_w		:= c_fotocoagulacao_laser.qt_potencia_inferior;
			qt_potencia_nasal_w			:= c_fotocoagulacao_laser.qt_potencia_nasal;
			qt_potencia_temporal_w		:= c_fotocoagulacao_laser.qt_potencia_temporal;
			qt_potencia_inf_nasal_w		:= c_fotocoagulacao_laser.qt_potencia_inf_nasal;
			qt_potencia_sup_nasal_w		:= c_fotocoagulacao_laser.qt_potencia_sup_nasal;
			qt_potencia_inf_temporal_w	:= c_fotocoagulacao_laser.qt_potencia_inf_temporal;
			qt_potencia_sup_temporal_w	:= c_fotocoagulacao_laser.qt_potencia_sup_temporal;
			qt_potencia_macula_w			:= c_fotocoagulacao_laser.qt_potencia_macula;
			dt_liberacao_w					:=	c_fotocoagulacao_laser.dt_liberacao;
			cd_profissional_w				:=	c_fotocoagulacao_laser.cd_profissional;
			end;
		end loop;
	else
		FOR c_fotocoagulacao_laser IN fotocoagulacao_laser_paciente LOOP
			begin
			ds_observacao_w				:= c_fotocoagulacao_laser.ds_observacao;
			ie_olho_w						:= c_fotocoagulacao_laser.ie_olho;
			ie_laser_w						:= c_fotocoagulacao_laser.ie_laser;
			nr_disparo_posterior_w		:= c_fotocoagulacao_laser.nr_disparo_posterior;
			nr_disparo_superior_w		:= c_fotocoagulacao_laser.nr_disparo_superior;
			nr_disparo_inferior_w		:= c_fotocoagulacao_laser.nr_disparo_inferior;
			nr_disparo_nasal_w			:= c_fotocoagulacao_laser.nr_disparo_nasal;
			nr_disparo_temporal_w		:= c_fotocoagulacao_laser.nr_disparo_temporal;
			nr_disparo_inf_nasal_w		:= c_fotocoagulacao_laser.nr_disparo_inf_nasal;
			nr_disparo_sup_nasal_w		:= c_fotocoagulacao_laser.nr_disparo_sup_nasal;
			nr_disparo_inf_temporal_w	:= c_fotocoagulacao_laser.nr_disparo_inf_temporal;
			nr_disparo_sup_temporal_w	:= c_fotocoagulacao_laser.nr_disparo_sup_temporal;
			nr_disparo_macula_w			:= c_fotocoagulacao_laser.nr_disparo_macula;
			nr_mira_w						:= c_fotocoagulacao_laser.nr_mira;
			qt_intervalo_w					:= c_fotocoagulacao_laser.qt_intervalo;
			qt_potencia_posterior_w		:= c_fotocoagulacao_laser.qt_potencia_posterior;
			qt_potencia_superior_w		:= c_fotocoagulacao_laser.qt_potencia_superior;
			qt_potencia_inferior_w		:= c_fotocoagulacao_laser.qt_potencia_inferior;
			qt_potencia_nasal_w			:= c_fotocoagulacao_laser.qt_potencia_nasal;
			qt_potencia_temporal_w		:= c_fotocoagulacao_laser.qt_potencia_temporal;
			qt_potencia_inf_nasal_w		:= c_fotocoagulacao_laser.qt_potencia_inf_nasal;
			qt_potencia_sup_nasal_w		:= c_fotocoagulacao_laser.qt_potencia_sup_nasal;
			qt_potencia_inf_temporal_w	:= c_fotocoagulacao_laser.qt_potencia_inf_temporal;
			qt_potencia_sup_temporal_w	:= c_fotocoagulacao_laser.qt_potencia_sup_temporal;
			qt_potencia_macula_w			:= c_fotocoagulacao_laser.qt_potencia_macula;
			cd_profissional_w				:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w						:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaFotocoagulacao.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaFotocoagulacao[i].ie_obter_resultado = 'S') then
			vListaFotocoagulacao[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaFotocoagulacao[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaFotocoagulacao[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaFotocoagulacao[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaFotocoagulacao[i].ds_valor	:=	ds_observacao_w;
				when 'IE_OLHO' then
					vListaFotocoagulacao[i].ds_valor	:=	ie_olho_w;
				when 'IE_LASER' then
					vListaFotocoagulacao[i].ds_valor	:=	ie_laser_w;
				when 'NR_DISPARO_POSTERIOR' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_posterior_w;
				when 'NR_DISPARO_SUPERIOR' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_superior_w;
				when 'NR_DISPARO_INFERIOR' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_inferior_w;
				when 'NR_DISPARO_NASAL' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_nasal_w;
				when 'NR_DISPARO_TEMPORAL' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_temporal_w;
				when 'NR_DISPARO_INF_NASAL' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_inf_nasal_w;
				when 'NR_DISPARO_SUP_NASAL' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_sup_nasal_w;
				when 'NR_DISPARO_INF_TEMPORAL' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_inf_temporal_w;
				when 'NR_DISPARO_SUP_TEMPORAL' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_sup_temporal_w;
				when 'NR_DISPARO_MACULA' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_disparo_macula_w;
				when 'NR_MIRA' then
					vListaFotocoagulacao[i].nr_valor	:=	nr_mira_w;
				when 'QT_INTERVALO' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_intervalo_w;
				when 'QT_POTENCIA_POSTERIOR' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_potencia_posterior_w;
				when 'QT_POTENCIA_SUPERIOR' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_potencia_superior_w;
				when 'QT_POTENCIA_INFERIOR' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_potencia_inferior_w;
				when 'QT_POTENCIA_NASAL' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_potencia_nasal_w;
				when 'QT_POTENCIA_TEMPORAL' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_potencia_temporal_w;
				when 'QT_POTENCIA_INF_NASAL' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_potencia_inf_nasal_w;
				when 'QT_POTENCIA_SUP_NASAL' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_potencia_sup_nasal_w;
				when 'QT_POTENCIA_INF_TEMPORAL' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_potencia_inf_temporal_w;
				when 'QT_POTENCIA_SUP_TEMPORAL' then
					vListaFotocoagulacao[i].nr_valor	:= qt_potencia_sup_temporal_w;
				when 'QT_POTENCIA_MACULA' then
					vListaFotocoagulacao[i].nr_valor	:=	qt_potencia_macula_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
