SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_iridectomia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaIridectomia INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w					oft_iridectomia.dt_registro%type;
ds_observacao_w			oft_iridectomia.ds_observacao%type;
ie_olho_w					oft_iridectomia.ie_olho%type;
ie_quadrante_w				oft_iridectomia.ie_quadrante%type;
qt_disparos_w 				oft_iridectomia.qt_disparos%type;
qt_disparo_sup_w			oft_iridectomia.qt_disparo_sup%type;
qt_disparo_inf_w			oft_iridectomia.qt_disparo_inf%type;
qt_disparo_tem_w			oft_iridectomia.qt_disparo_tem%type;
qt_disparo_nas_w			oft_iridectomia.qt_disparo_nas%type;
qt_potencia_sup_w			oft_iridectomia.qt_potencia_sup%type;
qt_potencia_inf_w			oft_iridectomia.qt_potencia_inf%type;
qt_potencia_tem_w			oft_iridectomia.qt_potencia_tem%type;
qt_potencia_nas_w			oft_iridectomia.qt_potencia_nas%type;
qt_energia_utilizada_w	oft_iridectomia.qt_energia_utilizada%type;
cd_profissional_w			oft_iridectomia.cd_profissional%TYPE;
dt_liberacao_w				timestamp;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
ds_erro_w					varchar(4000);

iridectomia_form CURSOR FOR
	SELECT	a.*
	from		oft_iridectomia a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_registro;

iridectomia_paciente CURSOR FOR
	SELECT	a.*
	from		oft_iridectomia a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_registro;
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaIridectomia.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_iridectomia IN iridectomia_form LOOP
			begin
			dt_exame_w					:= c_iridectomia.dt_registro;
			ds_observacao_w			:= c_iridectomia.ds_observacao;
			ie_olho_w					:= c_iridectomia.ie_olho;
			ie_quadrante_w				:= c_iridectomia.ie_quadrante;
			qt_disparos_w 				:= c_iridectomia.qt_disparos;
			qt_disparo_sup_w			:= c_iridectomia.qt_disparo_sup;
			qt_disparo_inf_w			:= c_iridectomia.qt_disparo_inf;
			qt_disparo_tem_w			:= c_iridectomia.qt_disparo_tem;
			qt_disparo_nas_w			:= c_iridectomia.qt_disparo_nas;
			qt_potencia_sup_w			:= c_iridectomia.qt_potencia_sup;
			qt_potencia_inf_w			:= c_iridectomia.qt_potencia_inf;
			qt_potencia_tem_w			:= c_iridectomia.qt_potencia_tem;
			qt_potencia_nas_w			:= c_iridectomia.qt_potencia_nas;
			qt_energia_utilizada_w	:= c_iridectomia.qt_energia_utilizada;
			dt_liberacao_w				:=	c_iridectomia.dt_liberacao;
			cd_profissional_w			:=	c_iridectomia.cd_profissional;
			end;
		end loop;
	else
		FOR c_iridectomia IN iridectomia_paciente LOOP
			begin
			ds_observacao_w			:= c_iridectomia.ds_observacao;
			ie_olho_w					:= c_iridectomia.ie_olho;
			ie_quadrante_w				:= c_iridectomia.ie_quadrante;
			qt_disparos_w 				:= c_iridectomia.qt_disparos;
			qt_disparo_sup_w			:= c_iridectomia.qt_disparo_sup;
			qt_disparo_inf_w			:= c_iridectomia.qt_disparo_inf;
			qt_disparo_tem_w			:= c_iridectomia.qt_disparo_tem;
			qt_disparo_nas_w			:= c_iridectomia.qt_disparo_nas;
			qt_potencia_sup_w			:= c_iridectomia.qt_potencia_sup;
			qt_potencia_inf_w			:= c_iridectomia.qt_potencia_inf;
			qt_potencia_tem_w			:= c_iridectomia.qt_potencia_tem;
			qt_potencia_nas_w			:= c_iridectomia.qt_potencia_nas;
			qt_energia_utilizada_w	:= c_iridectomia.qt_energia_utilizada;
			cd_profissional_w			:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w					:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaIridectomia.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaIridectomia[i].ie_obter_resultado = 'S') then
			vListaIridectomia[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaIridectomia[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaIridectomia[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaIridectomia[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaIridectomia[i].ds_valor	:= ds_observacao_w;
				when 'IE_OLHO' then
					vListaIridectomia[i].ds_valor	:= ie_olho_w;
				when 'IE_QUADRANTE' then
					vListaIridectomia[i].ds_valor	:= ie_quadrante_w;
				when 'QT_DISPAROS' then
					vListaIridectomia[i].nr_valor	:= qt_disparos_w;
				when 'QT_DISPARO_SUP' then
					vListaIridectomia[i].nr_valor	:= qt_disparo_sup_w;
				when 'QT_DISPARO_INF' then
					vListaIridectomia[i].nr_valor	:= qt_disparo_inf_w;
				when 'QT_DISPARO_TEM' then
					vListaIridectomia[i].nr_valor	:= qt_disparo_tem_w;
				when 'QT_DISPARO_NAS' then
					vListaIridectomia[i].nr_valor	:= qt_disparo_nas_w;
				when 'QT_POTENCIA_SUP' then
					vListaIridectomia[i].nr_valor	:= qt_potencia_sup_w;
				when 'QT_POTENCIA_INF' then
					vListaIridectomia[i].nr_valor	:= qt_potencia_inf_w;
				when 'QT_POTENCIA_TEM' then
					vListaIridectomia[i].nr_valor	:= qt_potencia_tem_w;
				when 'QT_POTENCIA_NAS' then
					vListaIridectomia[i].nr_valor	:= qt_potencia_nas_w;
				when 'QT_ENERGIA_UTILIZADA' then
					vListaIridectomia[i].nr_valor	:= qt_energia_utilizada_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_iridectomia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaIridectomia INOUT strRecTypeFormOft) FROM PUBLIC;

