CREATE OR REPLACE PROCEDURE oft_obter_oculos ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaOculos INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w				oft_oculos.dt_registro%type;
ds_observacao_w		oft_oculos.ds_observacao%type;
nr_seq_tipo_lente_w	oft_oculos.nr_seq_tipo_lente%type;
vl_od_pl_dio_esf_w	oft_oculos.vl_od_pl_dio_esf%type;
vl_od_pl_dio_cil_w	oft_oculos.vl_od_pl_dio_cil%type;
vl_od_pl_eixo_w		oft_oculos.vl_od_pl_eixo%type;
vl_od_pl_adicao_w		oft_oculos.vl_od_pl_adicao%type;
vl_oe_pl_dio_esf_w	oft_oculos.vl_oe_pl_dio_esf%type;
vl_oe_pl_dio_cil_w	oft_oculos.vl_oe_pl_dio_cil%type;
vl_oe_pl_eixo_w		oft_oculos.vl_oe_pl_eixo%type;
vl_oe_pl_adicao_w		oft_oculos.vl_oe_pl_adicao%type;
vl_od_pp_dio_esf_w	oft_oculos.vl_od_pp_dio_esf%type;
vl_od_pp_dio_cil_w	oft_oculos.vl_od_pp_dio_cil%type;
vl_od_pp_eixo_w		oft_oculos.vl_od_pp_eixo%type;
vl_od_pp_adicao_w		oft_oculos.vl_od_pp_adicao%type;
vl_oe_pp_dio_esf_w	oft_oculos.vl_oe_pp_dio_esf%type;
vl_oe_pp_dio_cil_w	oft_oculos.vl_oe_pp_dio_cil%type;
vl_oe_pp_eixo_w		oft_oculos.vl_oe_pp_eixo%type;
vl_oe_pp_adicao_w		oft_oculos.vl_oe_pp_adicao%type;
vl_od_in_dio_esf_w	oft_oculos.vl_od_in_dio_esf%type;
vl_od_in_dio_cil_w	oft_oculos.vl_od_in_dio_cil%type;
vl_oe_in_dio_esf_w	oft_oculos.vl_oe_in_dio_esf%type;
vl_oe_in_dio_cil_w	oft_oculos.vl_oe_in_dio_cil%type;
vl_oe_in_eixo_w		oft_oculos.vl_oe_in_eixo%type;
vl_oe_in_adicao_w		oft_oculos.vl_oe_in_adicao%type;
vl_od_in_eixo_w		oft_oculos.vl_od_in_eixo%type;
vl_od_in_adicao_w		oft_oculos.vl_od_in_adicao%type;
ds_orientacao_w		oft_oculos.ds_orientacao%type;
cd_profissional_w		oft_oculos.cd_profissional%TYPE;
dt_liberacao_w			timestamp;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w			usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w				varchar(4000);

oculos_form CURSOR FOR
	SELECT	a.*
	from		oft_oculos a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_registro;

oculos_paciente CURSOR FOR
	SELECT	a.*
	from		oft_oculos a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_registro;
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaOculos.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_oculos IN oculos_form LOOP
			begin
			dt_exame_w				:= c_oculos.dt_registro;
			ds_observacao_w		:= c_oculos.ds_observacao;
			nr_seq_tipo_lente_w	:= c_oculos.nr_seq_tipo_lente;
			vl_od_pl_dio_esf_w	:= c_oculos.vl_od_pl_dio_esf;
			vl_od_pl_dio_cil_w	:= c_oculos.vl_od_pl_dio_cil;
			vl_od_pl_eixo_w		:= c_oculos.vl_od_pl_eixo;
			vl_od_pl_adicao_w		:= c_oculos.vl_od_pl_adicao;
			vl_oe_pl_dio_esf_w	:= c_oculos.vl_oe_pl_dio_esf;
			vl_oe_pl_dio_cil_w	:= c_oculos.vl_oe_pl_dio_cil;
			vl_oe_pl_eixo_w		:= c_oculos.vl_oe_pl_eixo;
			vl_oe_pl_adicao_w		:= c_oculos.vl_oe_pl_adicao;
			vl_od_pp_dio_esf_w	:= c_oculos.vl_od_pp_dio_esf;
			vl_od_pp_dio_cil_w	:= c_oculos.vl_od_pp_dio_cil;
			vl_od_pp_eixo_w		:= c_oculos.vl_od_pp_eixo;
			vl_od_pp_adicao_w		:= c_oculos.vl_od_pp_adicao;
			vl_oe_pp_dio_esf_w	:= c_oculos.vl_oe_pp_dio_esf;
			vl_oe_pp_dio_cil_w	:= c_oculos.vl_oe_pp_dio_cil;
			vl_oe_pp_eixo_w		:= c_oculos.vl_oe_pp_eixo;
			vl_oe_pp_adicao_w		:= c_oculos.vl_oe_pp_adicao;
			vl_od_in_dio_esf_w	:= c_oculos.vl_od_in_dio_esf;
			vl_od_in_dio_cil_w	:= c_oculos.vl_od_in_dio_cil;
			vl_oe_in_dio_esf_w	:= c_oculos.vl_oe_in_dio_esf;
			vl_oe_in_dio_cil_w	:= c_oculos.vl_oe_in_dio_cil;
			vl_oe_in_eixo_w		:= c_oculos.vl_oe_in_eixo;
			vl_oe_in_adicao_w		:= c_oculos.vl_oe_in_adicao;
			vl_od_in_eixo_w		:= c_oculos.vl_od_in_eixo;
			vl_od_in_adicao_w		:= c_oculos.vl_od_in_adicao;
			ds_orientacao_w		:= c_oculos.ds_orientacao;
			dt_liberacao_w			:=	c_oculos.dt_liberacao;
			cd_profissional_w		:=	c_oculos.cd_profissional;
			end;
		end loop;
	else
		FOR c_oculos IN oculos_paciente LOOP
			begin
			ds_observacao_w		:= c_oculos.ds_observacao;
			nr_seq_tipo_lente_w	:= c_oculos.nr_seq_tipo_lente;
			vl_od_pl_dio_esf_w	:= c_oculos.vl_od_pl_dio_esf;
			vl_od_pl_dio_cil_w	:= c_oculos.vl_od_pl_dio_cil;
			vl_od_pl_eixo_w		:= c_oculos.vl_od_pl_eixo;
			vl_od_pl_adicao_w		:= c_oculos.vl_od_pl_adicao;
			vl_oe_pl_dio_esf_w	:= c_oculos.vl_oe_pl_dio_esf;
			vl_oe_pl_dio_cil_w	:= c_oculos.vl_oe_pl_dio_cil;
			vl_oe_pl_eixo_w		:= c_oculos.vl_oe_pl_eixo;
			vl_oe_pl_adicao_w		:= c_oculos.vl_oe_pl_adicao;
			vl_od_pp_dio_esf_w	:= c_oculos.vl_od_pp_dio_esf;
			vl_od_pp_dio_cil_w	:= c_oculos.vl_od_pp_dio_cil;
			vl_od_pp_eixo_w		:= c_oculos.vl_od_pp_eixo;
			vl_od_pp_adicao_w		:= c_oculos.vl_od_pp_adicao;
			vl_oe_pp_dio_esf_w	:= c_oculos.vl_oe_pp_dio_esf;
			vl_oe_pp_dio_cil_w	:= c_oculos.vl_oe_pp_dio_cil;
			vl_oe_pp_eixo_w		:= c_oculos.vl_oe_pp_eixo;
			vl_oe_pp_adicao_w		:= c_oculos.vl_oe_pp_adicao;
			vl_od_in_dio_esf_w	:= c_oculos.vl_od_in_dio_esf;
			vl_od_in_dio_cil_w	:= c_oculos.vl_od_in_dio_cil;
			vl_oe_in_dio_esf_w	:= c_oculos.vl_oe_in_dio_esf;
			vl_oe_in_dio_cil_w	:= c_oculos.vl_oe_in_dio_cil;
			vl_oe_in_eixo_w		:= c_oculos.vl_oe_in_eixo;
			vl_oe_in_adicao_w		:= c_oculos.vl_oe_in_adicao;
			vl_od_in_eixo_w		:= c_oculos.vl_od_in_eixo;
			vl_od_in_adicao_w		:= c_oculos.vl_od_in_adicao;
			ds_orientacao_w		:= c_oculos.ds_orientacao;
			cd_profissional_w		:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w				:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaOculos.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaOculos[i].ie_obter_resultado = 'S') then
			vListaOculos[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaOculos[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaOculos[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaOculos[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaOculos[i].ds_valor	:= ds_observacao_w;
				when 'NR_SEQ_TIPO_LENTE' then
					vListaOculos[i].nr_valor	:= nr_seq_tipo_lente_w;
				when 'VL_OD_PL_DIO_ESF' then
					vListaOculos[i].nr_valor	:= vl_od_pl_dio_esf_w;
				when 'VL_OD_PL_DIO_CIL' then
					vListaOculos[i].nr_valor	:= vl_od_pl_dio_cil_w;
				when 'VL_OD_PL_EIXO' then
					vListaOculos[i].nr_valor	:= vl_od_pl_eixo_w;
				when 'VL_OD_PL_ADICAO' then
					vListaOculos[i].nr_valor	:= vl_od_pl_adicao_w;
				when 'VL_OE_PL_DIO_ESF' then
					vListaOculos[i].nr_valor	:= vl_oe_pl_dio_esf_w;
				when 'VL_OE_PL_DIO_CIL' then
					vListaOculos[i].nr_valor	:= vl_oe_pl_dio_cil_w;
				when 'VL_OE_PL_EIXO' then
					vListaOculos[i].nr_valor	:= vl_oe_pl_eixo_w;
				when 'VL_OE_PL_ADICAO' then
					vListaOculos[i].nr_valor	:= vl_oe_pl_adicao_w;
				when 'VL_OD_PP_DIO_ESF' then
					vListaOculos[i].nr_valor	:= vl_od_pp_dio_esf_w;
				when 'VL_OD_PP_DIO_CIL' then
					vListaOculos[i].nr_valor	:= vl_od_pp_dio_cil_w;
				when 'VL_OD_PP_EIXO' then
					vListaOculos[i].nr_valor	:= vl_od_pp_eixo_w;
				when 'VL_OD_PP_ADICAO' then
					vListaOculos[i].nr_valor	:= vl_od_pp_adicao_w;
				when 'VL_OE_PP_DIO_ESF' then
					vListaOculos[i].nr_valor	:= vl_oe_pp_dio_esf_w;
				when 'VL_OE_PP_DIO_CIL' then
					vListaOculos[i].nr_valor	:= vl_oe_pp_dio_cil_w;
				when 'VL_OE_PP_EIXO' then
					vListaOculos[i].nr_valor	:= vl_oe_pp_eixo_w;
				when 'VL_OE_PP_ADICAO' then
					vListaOculos[i].nr_valor	:= vl_oe_pp_adicao_w;
				when 'VL_OD_IN_DIO_ESF' then
					vListaOculos[i].nr_valor	:= vl_od_in_dio_esf_w;
				when 'VL_OD_IN_DIO_CIL' then
					vListaOculos[i].nr_valor	:= vl_od_in_dio_cil_w;
				when 'VL_OE_IN_DIO_ESF' then
					vListaOculos[i].nr_valor	:= vl_oe_in_dio_esf_w;
				when 'VL_OE_IN_DIO_CIL' then
					vListaOculos[i].nr_valor	:= vl_oe_in_dio_cil_w;
				when 'VL_OE_IN_EIXO' then
					vListaOculos[i].nr_valor	:= vl_oe_in_eixo_w;
				when 'VL_OE_IN_ADICAO' then
					vListaOculos[i].nr_valor	:= vl_oe_in_adicao_w;
				when 'VL_OD_IN_EIXO' then
					vListaOculos[i].nr_valor	:= vl_od_in_eixo_w;
				when 'VL_OD_IN_ADICAO' then
					vListaOculos[i].nr_valor	:= vl_od_in_adicao_w;
				when 'DS_ORIENTACAO' then
					vListaOculos[i].ds_valor	:= ds_orientacao_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
