SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_oculos ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaOculos INOUT strRecTypeFormOft) FROM PUBLIC;

