SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_paquimetria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaPaquimetria INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w						oft_paquimetria.dt_registro%type;
ds_observacao_w				oft_paquimetria.ds_observacao%type;
qt_od_paqui_sup_w				oft_paquimetria.qt_od_paqui_sup%type;
qt_od_paqui_temp_w			oft_paquimetria.qt_od_paqui_temp%type;
qt_od_paqui_inf_w				oft_paquimetria.qt_od_paqui_inf%type;
qt_od_paqui_nasal_w			oft_paquimetria.qt_od_paqui_nasal%type;
qt_od_paqui_central_w		oft_paquimetria.qt_od_paqui_central%type;
qt_oe_paqui_sup_w				oft_paquimetria.qt_oe_paqui_sup%type;
qt_oe_paqui_temp_w			oft_paquimetria.qt_oe_paqui_temp%type;
qt_oe_paqui_inf_w				oft_paquimetria.qt_oe_paqui_inf%type;
qt_oe_paqui_nasal_w			oft_paquimetria.qt_oe_paqui_nasal%type;
qt_oe_paqui_central_w		oft_paquimetria.qt_oe_paqui_central%type;
qt_avg_od_w						oft_paquimetria.qt_avg_od%type;
qt_avg_oe_w						oft_paquimetria.qt_avg_oe%type;
qt_od_paqui_temp_sup_w		oft_paquimetria.qt_od_paqui_temp_sup%type;
qt_od_paqui_nasal_sup_w		oft_paquimetria.qt_od_paqui_nasal_sup%type;
qt_od_paqui_temp_inf_w		oft_paquimetria.qt_od_paqui_temp_inf%type;
qt_od_paqui_nasal_inf_w		oft_paquimetria.qt_od_paqui_nasal_inf%type;
qt_oe_paqui_temp_sup_w		oft_paquimetria.qt_oe_paqui_temp_sup%type;
qt_oe_paqui_nasal_sup_w		oft_paquimetria.qt_oe_paqui_nasal_sup%type;
qt_oe_paqui_temp_inf_w		oft_paquimetria.qt_oe_paqui_temp_inf%type;
qt_oe_paqui_nasal_inf_w		oft_paquimetria.qt_oe_paqui_nasal_inf%type;
ie_tipo_paquimetria_w		oft_paquimetria.ie_tipo_paquimetria%type;
cd_profissional_w				oft_paquimetria.cd_profissional%TYPE;
dt_liberacao_w					timestamp;
cd_estabelecimento_w			estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w					usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w						varchar(4000);

paquimetria_form CURSOR FOR
	SELECT	a.*
	from		oft_paquimetria a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		coalesce(ie_tipo_paquimetria,'S')	=	coalesce(ie_tipo_paquimetria_w,'S')
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_registro;

paquimetria_paciente CURSOR FOR
	SELECT	a.*
	from		oft_paquimetria a,
				oft_consulta b
	where		a.nr_seq_consulta					=	b.nr_sequencia
	and		coalesce(ie_tipo_paquimetria,'S')	=	coalesce(ie_tipo_paquimetria_w,'S')
	and		b.cd_pessoa_fisica				=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 					<> nr_seq_consulta_p
	order by dt_registro;
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vlistapaquimetria.count > 0) then
	for i in 1..vListaPaquimetria.count loop
		begin
		if (vListaPaquimetria[i].nr_seq_visao = 86144) then
			ie_tipo_paquimetria_w := 'U';
		elsif (vListaPaquimetria[i].nr_seq_visao = 86143) then
			ie_tipo_paquimetria_w := 'S';
		end if;
		end;
	end loop;
	if (ie_opcao_p = 'F') then
		FOR c_paquimetria IN paquimetria_form LOOP
			begin
			dt_exame_w						:= c_paquimetria.dt_registro;
			ds_observacao_w				:= c_paquimetria.ds_observacao;
			qt_od_paqui_sup_w				:= c_paquimetria.qt_od_paqui_sup;
			qt_od_paqui_temp_w			:= c_paquimetria.qt_od_paqui_temp;
			qt_od_paqui_inf_w				:= c_paquimetria.qt_od_paqui_inf;
			qt_od_paqui_nasal_w			:= c_paquimetria.qt_od_paqui_nasal;
			qt_od_paqui_central_w		:= c_paquimetria.qt_od_paqui_central;
			qt_oe_paqui_sup_w				:= c_paquimetria.qt_oe_paqui_sup;
			qt_oe_paqui_temp_w			:= c_paquimetria.qt_oe_paqui_temp;
			qt_oe_paqui_inf_w				:= c_paquimetria.qt_oe_paqui_inf;
			qt_oe_paqui_nasal_w			:= c_paquimetria.qt_oe_paqui_nasal;
			qt_oe_paqui_central_w		:= c_paquimetria.qt_oe_paqui_central;
			qt_avg_od_w						:= c_paquimetria.qt_avg_od;
			qt_avg_oe_w						:= c_paquimetria.qt_avg_oe;
			qt_od_paqui_temp_sup_w		:= c_paquimetria.qt_od_paqui_temp_sup;
			qt_od_paqui_nasal_sup_w		:= c_paquimetria.qt_od_paqui_nasal_sup;
			qt_od_paqui_temp_inf_w		:= c_paquimetria.qt_od_paqui_temp_inf;
			qt_od_paqui_nasal_inf_w		:= c_paquimetria.qt_od_paqui_nasal_inf;
			qt_oe_paqui_temp_sup_w		:= c_paquimetria.qt_oe_paqui_temp_sup;
			qt_oe_paqui_nasal_sup_w		:= c_paquimetria.qt_oe_paqui_nasal_sup;
			qt_oe_paqui_temp_inf_w		:= c_paquimetria.qt_oe_paqui_temp_inf;
			qt_oe_paqui_nasal_inf_w		:= c_paquimetria.qt_oe_paqui_nasal_inf;
			ie_tipo_paquimetria_w		:= c_paquimetria.ie_tipo_paquimetria;
			dt_liberacao_w					:=	c_paquimetria.dt_liberacao;
			cd_profissional_w				:=	c_paquimetria.cd_profissional;
			end;
		end loop;
	else
		FOR c_paquimetria IN paquimetria_paciente LOOP
			begin
			ds_observacao_w				:= c_paquimetria.ds_observacao;
			qt_od_paqui_sup_w				:= c_paquimetria.qt_od_paqui_sup;
			qt_od_paqui_temp_w			:= c_paquimetria.qt_od_paqui_temp;
			qt_od_paqui_inf_w				:= c_paquimetria.qt_od_paqui_inf;
			qt_od_paqui_nasal_w			:= c_paquimetria.qt_od_paqui_nasal;
			qt_od_paqui_central_w		:= c_paquimetria.qt_od_paqui_central;
			qt_oe_paqui_sup_w				:= c_paquimetria.qt_oe_paqui_sup;
			qt_oe_paqui_temp_w			:= c_paquimetria.qt_oe_paqui_temp;
			qt_oe_paqui_inf_w				:= c_paquimetria.qt_oe_paqui_inf;
			qt_oe_paqui_nasal_w			:= c_paquimetria.qt_oe_paqui_nasal;
			qt_oe_paqui_central_w		:= c_paquimetria.qt_oe_paqui_central;
			qt_avg_od_w						:= c_paquimetria.qt_avg_od;
			qt_avg_oe_w						:= c_paquimetria.qt_avg_oe;
			qt_od_paqui_temp_sup_w		:= c_paquimetria.qt_od_paqui_temp_sup;
			qt_od_paqui_nasal_sup_w		:= c_paquimetria.qt_od_paqui_nasal_sup;
			qt_od_paqui_temp_inf_w		:= c_paquimetria.qt_od_paqui_temp_inf;
			qt_od_paqui_nasal_inf_w		:= c_paquimetria.qt_od_paqui_nasal_inf;
			qt_oe_paqui_temp_sup_w		:= c_paquimetria.qt_oe_paqui_temp_sup;
			qt_oe_paqui_nasal_sup_w		:= c_paquimetria.qt_oe_paqui_nasal_sup;
			qt_oe_paqui_temp_inf_w		:= c_paquimetria.qt_oe_paqui_temp_inf;
			qt_oe_paqui_nasal_inf_w		:= c_paquimetria.qt_oe_paqui_nasal_inf;
			ie_tipo_paquimetria_w		:= c_paquimetria.ie_tipo_paquimetria;
			cd_profissional_w				:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w						:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaPaquimetria.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaPaquimetria[i].ie_obter_resultado = 'S') then
			vListaPaquimetria[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaPaquimetria[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaPaquimetria[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaPaquimetria[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaPaquimetria[i].ds_valor	:= ds_observacao_w;
				when 'QT_OD_PAQUI_SUP' then
					vListaPaquimetria[i].nr_valor	:= qt_od_paqui_sup_w;
				when 'QT_OD_PAQUI_TEMP' then
					vListaPaquimetria[i].nr_valor	:= qt_od_paqui_temp_w;
				when 'QT_OD_PAQUI_INF' then
					vListaPaquimetria[i].nr_valor	:= qt_od_paqui_inf_w;
				when 'QT_OD_PAQUI_NASAL' then
					vListaPaquimetria[i].nr_valor	:= qt_od_paqui_nasal_w;
				when 'QT_OD_PAQUI_CENTRAL' then
					vListaPaquimetria[i].nr_valor	:= qt_od_paqui_central_w;
				when 'QT_OE_PAQUI_SUP' then
					vListaPaquimetria[i].nr_valor	:= qt_oe_paqui_sup_w;
				when 'QT_OE_PAQUI_TEMP' then
					vListaPaquimetria[i].nr_valor	:= qt_oe_paqui_temp_w;
				when 'QT_OE_PAQUI_INF' then
					vListaPaquimetria[i].nr_valor	:= qt_oe_paqui_inf_w;
				when 'QT_OE_PAQUI_NASAL' then
					vListaPaquimetria[i].nr_valor	:= qt_oe_paqui_nasal_w;
				when 'QT_OE_PAQUI_CENTRAL' then
					vListaPaquimetria[i].nr_valor	:= qt_oe_paqui_central_w;
				when 'QT_AVG_OD' then
					vListaPaquimetria[i].nr_valor	:= qt_avg_od_w;
				when 'QT_AVG_OE' then
					vListaPaquimetria[i].nr_valor	:= qt_avg_oe_w;
				when 'QT_OD_PAQUI_TEMP_SUP' then
					vListaPaquimetria[i].nr_valor := qt_od_paqui_temp_sup_w;
				when 'QT_OD_PAQUI_NASAL_SUP' then
					vListaPaquimetria[i].nr_valor	:= qt_od_paqui_nasal_sup_w;
				when 'QT_OD_PAQUI_TEMP_INF' then
					vListaPaquimetria[i].nr_valor	:= qt_od_paqui_temp_inf_w;
				when 'QT_OD_PAQUI_NASAL_INF' then
					vListaPaquimetria[i].nr_valor	:= qt_od_paqui_nasal_inf_w;
				when 'QT_OE_PAQUI_TEMP_SUP' then
					vListaPaquimetria[i].nr_valor	:= qt_oe_paqui_temp_sup_w;
				when 'QT_OE_PAQUI_NASAL_SUP' then
					vListaPaquimetria[i].nr_valor	:= qt_oe_paqui_nasal_sup_w;
				when 'QT_OE_PAQUI_TEMP_INF' then
					vListaPaquimetria[i].nr_valor	:= qt_oe_paqui_temp_inf_w;
				when 'QT_OE_PAQUI_NASAL_INF' then
					vListaPaquimetria[i].nr_valor	:= qt_oe_paqui_nasal_inf_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_paquimetria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaPaquimetria INOUT strRecTypeFormOft) FROM PUBLIC;

