SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_pupilometria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaPupilometria INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w					oft_pupilometria.dt_registro%type;
ds_observacao_w			oft_pupilometria.ds_observacao%type;
qt_od_pupilometria_w		oft_pupilometria.qt_od_pupilometria%type;
qt_oe_pupilometria_w		oft_pupilometria.qt_oe_pupilometria%type;
ie_olho_dominante_w		oft_pupilometria.ie_olho_dominante%type;
qt_tamanho_corneano_w	oft_pupilometria.qt_tamanho_corneano%type;
cd_profissional_w			oft_pupilometria.cd_profissional%TYPE;
dt_liberacao_w				timestamp;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w					varchar(4000);

pupilometria_form CURSOR FOR
	SELECT	a.*
	from		oft_pupilometria a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_registro;

pupilometria_paciente CURSOR FOR
	SELECT	a.*
	from		oft_pupilometria a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_registro;
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaPupilometria.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_pupilometria IN pupilometria_form LOOP
			begin
			dt_exame_w					:=	c_pupilometria.dt_registro;
			ds_observacao_w			:=	c_pupilometria.ds_observacao;
			qt_od_pupilometria_w		:=	c_pupilometria.qt_od_pupilometria;
			qt_oe_pupilometria_w		:=	c_pupilometria.qt_oe_pupilometria;
			ie_olho_dominante_w		:=	c_pupilometria.ie_olho_dominante;
			qt_tamanho_corneano_w	:=	c_pupilometria.qt_tamanho_corneano;
			dt_liberacao_w				:=	c_pupilometria.dt_liberacao;
			cd_profissional_w			:=	c_pupilometria.cd_profissional;
			end;
		end loop;
	else
		FOR c_pupilometria IN pupilometria_paciente LOOP
			begin
			ds_observacao_w			:=	c_pupilometria.ds_observacao;
			qt_od_pupilometria_w		:=	c_pupilometria.qt_od_pupilometria;
			qt_oe_pupilometria_w		:=	c_pupilometria.qt_oe_pupilometria;
			ie_olho_dominante_w		:=	c_pupilometria.ie_olho_dominante;
			qt_tamanho_corneano_w	:=	c_pupilometria.qt_tamanho_corneano;
			cd_profissional_w			:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w					:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaPupilometria.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaPupilometria[i].ie_obter_resultado = 'S') then
			vListaPupilometria[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaPupilometria[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaPupilometria[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaPupilometria[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaPupilometria[i].ds_valor	:= ds_observacao_w;
				when 'QT_OD_PUPILOMETRIA' then
					vListaPupilometria[i].nr_valor	:= qt_od_pupilometria_w;
				when 'QT_OE_PUPILOMETRIA' then
					vListaPupilometria[i].nr_valor	:= qt_oe_pupilometria_w;
				when 'IE_OLHO_DOMINANTE' then
					vListaPupilometria[i].ds_valor	:= ie_olho_dominante_w;
				when 'QT_TAMANHO_CORNEANO' then
					vListaPupilometria[i].nr_valor	:= qt_tamanho_corneano_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_pupilometria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaPupilometria INOUT strRecTypeFormOft) FROM PUBLIC;

