            ds_ref_acomod_od_w    := c_refracao.ds_ref_acomod_od;
            ds_ref_acomod_oe_w    := c_refracao.ds_ref_acomod_oe;
			end;
		end loop;	
	end if;
	
	for i in 1..vListaRefracao.count loop
		begin
