			vListaRefracao[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaRefracao[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaRefracao[i].ds_valor	:= cd_profissional_w;
				when 'DT_EXAME' then
					vListaRefracao[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaRefracao[i].ds_valor	:=	ds_observacao_w;
				when 'IE_RECEITA_DINAMICA' then
