SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_sobrecarga_hidrica ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaSobrecargaHidrica INOUT strRecTypeFormOft) AS $body$
DECLARE


dt_exame_w					oft_sobrecarga_hidrica.dt_registro%type;
ds_observacao_w			oft_sobrecarga_hidrica.ds_observacao%type;
nr_medicao_previa_od_w	oft_sobrecarga_hidrica.nr_medicao_previa_od%type;
nr_medicao_previa_oe_w	oft_sobrecarga_hidrica.nr_medicao_previa_oe%type;
dt_medicao_previa_w		oft_sobrecarga_hidrica.dt_medicao_previa%type;
qt_agua_w					oft_sobrecarga_hidrica.qt_agua%type;
nr_prim_medicao_w			oft_sobrecarga_hidrica.nr_prim_medicao%type;
nr_prim_medicao_od_w		oft_sobrecarga_hidrica.nr_prim_medicao_od%type;
nr_prim_medicao_oe_w		oft_sobrecarga_hidrica.nr_prim_medicao_oe%type;
dt_prim_medicao_w			oft_sobrecarga_hidrica.dt_prim_medicao%type;
nr_seg_medicao_od_w		oft_sobrecarga_hidrica.nr_seg_medicao_od%type;
nr_seg_medicao_w			oft_sobrecarga_hidrica.nr_seg_medicao%type;
nr_seg_medicao_oe_w		oft_sobrecarga_hidrica.nr_seg_medicao_oe%type;
dt_seg_medicao_w			oft_sobrecarga_hidrica.dt_seg_medicao%type;
nr_ter_medicao_w			oft_sobrecarga_hidrica.nr_ter_medicao%type;
nr_ter_medicao_od_w		oft_sobrecarga_hidrica.nr_ter_medicao_od%type;
nr_ter_medicao_oe_w		oft_sobrecarga_hidrica.nr_ter_medicao_oe%type;
dt_ter_medicao_w			oft_sobrecarga_hidrica.dt_ter_medicao%type;
nr_qua_medicao_w			oft_sobrecarga_hidrica.nr_qua_medicao%type;
nr_qua_medicao_od_w		oft_sobrecarga_hidrica.nr_qua_medicao_od%type;
nr_qua_medicao_oe_w		oft_sobrecarga_hidrica.nr_qua_medicao_oe%type;
dt_qua_medicao_w			oft_sobrecarga_hidrica.dt_qua_medicao%type;
dt_ingestao_hidrica_w	oft_sobrecarga_hidrica.dt_ingestao_hidrica%type;
cd_profissional_w			oft_sobrecarga_hidrica.cd_profissional%TYPE;
dt_liberacao_w				timestamp;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w					varchar(4000);

sobrecarga_hidrica_form CURSOR FOR
	SELECT	a.*
	from		oft_sobrecarga_hidrica a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_registro;

sobrecarga_hidrica_paciente CURSOR FOR
	SELECT	a.*
	from		oft_sobrecarga_hidrica a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_registro;
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaSobrecargaHidrica.count > 0) then
	if (ie_opcao_p = 'F') then
		FOR c_sobrecarga_hidrica IN sobrecarga_hidrica_form LOOP
			begin
			dt_exame_w					:= c_sobrecarga_hidrica.dt_registro;
			ds_observacao_w			:= c_sobrecarga_hidrica.ds_observacao;
			nr_medicao_previa_od_w	:= c_sobrecarga_hidrica.nr_medicao_previa_od;
			nr_medicao_previa_oe_w	:= c_sobrecarga_hidrica.nr_medicao_previa_oe;
			dt_medicao_previa_w		:= c_sobrecarga_hidrica.dt_medicao_previa;
			qt_agua_w					:= c_sobrecarga_hidrica.qt_agua;
			nr_prim_medicao_w			:= c_sobrecarga_hidrica.nr_prim_medicao;
			nr_prim_medicao_od_w		:= c_sobrecarga_hidrica.nr_prim_medicao_od;
			nr_prim_medicao_oe_w		:= c_sobrecarga_hidrica.nr_prim_medicao_oe;
			dt_prim_medicao_w			:= c_sobrecarga_hidrica.dt_prim_medicao;
			nr_seg_medicao_od_w		:= c_sobrecarga_hidrica.nr_seg_medicao_od;
			nr_seg_medicao_w			:= c_sobrecarga_hidrica.nr_seg_medicao;
			nr_seg_medicao_oe_w		:= c_sobrecarga_hidrica.nr_seg_medicao_oe;
			dt_seg_medicao_w			:= c_sobrecarga_hidrica.dt_seg_medicao;
			nr_ter_medicao_w			:= c_sobrecarga_hidrica.nr_ter_medicao;
			nr_ter_medicao_od_w		:= c_sobrecarga_hidrica.nr_ter_medicao_od;
			nr_ter_medicao_oe_w		:= c_sobrecarga_hidrica.nr_ter_medicao_oe;
			dt_ter_medicao_w			:= c_sobrecarga_hidrica.dt_ter_medicao;
			nr_qua_medicao_w			:= c_sobrecarga_hidrica.nr_qua_medicao;
			nr_qua_medicao_od_w		:= c_sobrecarga_hidrica.nr_qua_medicao_od;
			nr_qua_medicao_oe_w		:= c_sobrecarga_hidrica.nr_qua_medicao_oe;
			dt_qua_medicao_w			:= c_sobrecarga_hidrica.dt_qua_medicao;
			dt_ingestao_hidrica_w	:= c_sobrecarga_hidrica.dt_ingestao_hidrica;
			dt_liberacao_w				:=	c_sobrecarga_hidrica.dt_liberacao;
			cd_profissional_w			:=	c_sobrecarga_hidrica.cd_profissional;
			end;
		end loop;
	else
		FOR c_sobrecarga_hidrica IN sobrecarga_hidrica_paciente LOOP
			begin
			ds_observacao_w			:= c_sobrecarga_hidrica.ds_observacao;
			nr_medicao_previa_od_w	:= c_sobrecarga_hidrica.nr_medicao_previa_od;
			nr_medicao_previa_oe_w	:= c_sobrecarga_hidrica.nr_medicao_previa_oe;
			dt_medicao_previa_w		:= c_sobrecarga_hidrica.dt_medicao_previa;
			qt_agua_w					:= c_sobrecarga_hidrica.qt_agua;
			nr_prim_medicao_w			:= c_sobrecarga_hidrica.nr_prim_medicao;
			nr_prim_medicao_od_w		:= c_sobrecarga_hidrica.nr_prim_medicao_od;
			nr_prim_medicao_oe_w		:= c_sobrecarga_hidrica.nr_prim_medicao_oe;
			dt_prim_medicao_w			:= c_sobrecarga_hidrica.dt_prim_medicao;
			nr_seg_medicao_od_w		:= c_sobrecarga_hidrica.nr_seg_medicao_od;
			nr_seg_medicao_w			:= c_sobrecarga_hidrica.nr_seg_medicao;
			nr_seg_medicao_oe_w		:= c_sobrecarga_hidrica.nr_seg_medicao_oe;
			dt_seg_medicao_w			:= c_sobrecarga_hidrica.dt_seg_medicao;
			nr_ter_medicao_w			:= c_sobrecarga_hidrica.nr_ter_medicao;
			nr_ter_medicao_od_w		:= c_sobrecarga_hidrica.nr_ter_medicao_od;
			nr_ter_medicao_oe_w		:= c_sobrecarga_hidrica.nr_ter_medicao_oe;
			dt_ter_medicao_w			:= c_sobrecarga_hidrica.dt_ter_medicao;
			nr_qua_medicao_w			:= c_sobrecarga_hidrica.nr_qua_medicao;
			nr_qua_medicao_od_w		:= c_sobrecarga_hidrica.nr_qua_medicao_od;
			nr_qua_medicao_oe_w		:= c_sobrecarga_hidrica.nr_qua_medicao_oe;
			dt_qua_medicao_w			:= c_sobrecarga_hidrica.dt_qua_medicao;
			dt_ingestao_hidrica_w	:= c_sobrecarga_hidrica.dt_ingestao_hidrica;
			cd_profissional_w			:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w					:= clock_timestamp();
			end;
		end loop;
	end if;

	for i in 1..vListaSobrecargaHidrica.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaSobrecargaHidrica[i].ie_obter_resultado = 'S') then
			vListaSobrecargaHidrica[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaSobrecargaHidrica[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaSobrecargaHidrica[i].ds_valor	:= cd_profissional_w;
				when 'DT_REGISTRO' then
					vListaSobrecargaHidrica[i].dt_valor	:= dt_exame_w;
				when 'DS_OBSERVACAO' then
					vListaSobrecargaHidrica[i].ds_valor	:= ds_observacao_w;
				when 'NR_MEDICAO_PREVIA_OD' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_medicao_previa_od_w;
				when 'NR_MEDICAO_PREVIA_OE' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_medicao_previa_oe_w;
				when 'DT_MEDICAO_PREVIA' then
					vListaSobrecargaHidrica[i].dt_valor	:= dt_medicao_previa_w;
				when 'QT_AGUA' then
					vListaSobrecargaHidrica[i].nr_valor	:= qt_agua_w;
				when 'NR_PRIM_MEDICAO' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_prim_medicao_w;
				when 'NR_PRIM_MEDICAO_OD' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_prim_medicao_od_w;
				when 'NR_PRIM_MEDICAO_OE' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_prim_medicao_oe_w;
				when 'DT_PRIM_MEDICAO' then
					vListaSobrecargaHidrica[i].dt_valor	:= dt_prim_medicao_w;
				when 'NR_SEG_MEDICAO_OD' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_seg_medicao_od_w;
				when 'NR_SEG_MEDICAO' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_seg_medicao_w;
				when 'NR_SEG_MEDICAO_OE' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_seg_medicao_oe_w;
				when 'DT_SEG_MEDICAO' then
					vListaSobrecargaHidrica[i].dt_valor	:= dt_seg_medicao_w;
				when 'NR_TER_MEDICAO' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_ter_medicao_w;
				when 'NR_TER_MEDICAO_OD' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_ter_medicao_od_w;
				when 'NR_TER_MEDICAO_OE' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_ter_medicao_oe_w;
				when 'DT_TER_MEDICAO' then
					vListaSobrecargaHidrica[i].dt_valor	:= dt_ter_medicao_w;
				when 'NR_QUA_MEDICAO' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_qua_medicao_w;
				when 'NR_QUA_MEDICAO_OD' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_qua_medicao_od_w;
				when 'NR_QUA_MEDICAO_OE' then
					vListaSobrecargaHidrica[i].nr_valor	:= nr_qua_medicao_oe_w;
				when 'DT_QUA_MEDICAO' then
					vListaSobrecargaHidrica[i].dt_valor	:= dt_qua_medicao_w;
				when 'DT_INGESTAO_HIDRICA' then
					vListaSobrecargaHidrica[i].dt_valor	:= dt_ingestao_hidrica_w;
				else
					null;
			end case;
		end if;
	end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_sobrecarga_hidrica ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaSobrecargaHidrica INOUT strRecTypeFormOft) FROM PUBLIC;

