SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_obter_tonometria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaTonometria INOUT strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w			oft_tonometria.nr_sequencia%type;
cd_equipamento_w		oft_tonometria.cd_equipamento%type;
ds_observacao_w		oft_tonometria.ds_observacao%type;
dt_exame_w				oft_tonometria.dt_exame%type;
ie_tipo_tonometria_w	oft_tonometria.ie_tipo_tonometria%type := 0;
vl_pressao_od_w		oft_tonometria.vl_pressao_od%type;
vl_pressao_oe_w		oft_tonometria.vl_pressao_oe%type;
cd_profissional_w		oft_tonometria.cd_profissional%TYPE;
dt_liberacao_w			timestamp;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w			usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ds_erro_w				varchar(4000);

tonometria_form CURSOR FOR
	SELECT	a.*
	from		oft_tonometria a,
				oft_consulta_formulario b
	where		a.nr_seq_consulta_form 	=	b.nr_sequencia
	and		a.nr_seq_consulta_form 	=	nr_seq_consulta_form_p
	and		a.nr_seq_consulta			=	nr_seq_consulta_p
	and		ie_tipo_tonometria		=	ie_tipo_tonometria_w
	and		((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.nm_usuario = nm_usuario_w))
	and		((coalesce(a.dt_inativacao::text, '') = '') or (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
	order by dt_exame;

tonometria_paciente CURSOR FOR
	SELECT	a.*
	from		oft_tonometria a,
				oft_consulta b
	where		a.nr_seq_consulta		=	b.nr_sequencia
	and		ie_tipo_tonometria	=	ie_tipo_tonometria_w
	and		b.cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and		b.nr_sequencia 		<> nr_seq_consulta_p
	order by dt_exame;
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaTonometria.count > 0) then
	for i in 1..vListaTonometria.count loop
		begin
		if (vListaTonometria[i].nr_seq_visao in (21953,88268)) then
			ie_tipo_tonometria_w := 2;
		elsif (vListaTonometria[i].nr_seq_visao = 86159) then
			ie_tipo_tonometria_w := 1;
		end if;
		end;
	end loop;
	if (ie_opcao_p = 'F') then
		FOR c_tonometria IN tonometria_form LOOP
			begin
			cd_equipamento_w			:=	c_tonometria.cd_equipamento;
			ds_observacao_w			:=	c_tonometria.ds_observacao;
			dt_exame_w					:=	c_tonometria.dt_exame;
			ie_tipo_tonometria_w		:=	c_tonometria.ie_tipo_tonometria;
			vl_pressao_od_w			:=	c_tonometria.vl_pressao_od;
			vl_pressao_oe_w			:=	c_tonometria.vl_pressao_oe;
			dt_liberacao_w				:=	c_tonometria.dt_liberacao;
			cd_profissional_w			:=	c_tonometria.cd_profissional;
			end;
		end loop;
	else
		FOR c_tonometria IN tonometria_paciente LOOP
			begin
			cd_equipamento_w			:=	c_tonometria.cd_equipamento;
			ds_observacao_w			:=	c_tonometria.ds_observacao;
			ie_tipo_tonometria_w		:=	c_tonometria.ie_tipo_tonometria;
			vl_pressao_od_w			:=	c_tonometria.vl_pressao_od;
			vl_pressao_oe_w			:=	c_tonometria.vl_pressao_oe;
			cd_profissional_w			:=	obter_pf_usuario(nm_usuario_w,'C');
			dt_exame_w					:= clock_timestamp();
			end;
		end loop;
	end if;
	for i in 1..vListaTonometria.count loop
		begin
		if (ie_opcao_p = 'F') or (vListaTonometria[i].ie_obter_resultado = 'S') then
			vListaTonometria[i].dt_liberacao	:= dt_liberacao_w;
			case upper(vListaTonometria[i].nm_campo)
				WHEN 'CD_PROFISSIONAL' THEN
					vListaTonometria[i].ds_valor	:= cd_profissional_w;
				when 'DT_EXAME' then
					vListaTonometria[i].dt_valor	:=	dt_exame_w;
				when 'CD_EQUIPAMENTO' then
					vListaTonometria[i].nr_valor	:=	cd_equipamento_w;
				when 'DS_OBSERVACAO' then
					vListaTonometria[i].ds_valor	:=	ds_observacao_w;
				when 'VL_PRESSAO_OD' then
					vListaTonometria[i].nr_valor	:=	vl_pressao_od_w;
				when 'VL_PRESSAO_OE' then
					vListaTonometria[i].nr_valor	:=	vl_pressao_oe_w;
				else
					null;
			end case;
		end if;
		end;
	end loop;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_obter_tonometria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, vListaTonometria INOUT strRecTypeFormOft) FROM PUBLIC;

