CREATE OR REPLACE PROCEDURE onc_consistir_acesso_pep ( nm_usuario_p text, cd_estabelecimento_p bigint, cd_paciente_p text, cd_pessoa_fisica_p text, dt_logon_p timestamp, ie_permite_p INOUT text, nr_seq_mensagem_p INOUT bigint) AS $body$
DECLARE

					 
nr_atendimento_w	bigint;
ie_permite_w		varchar(5);
nr_seq_mensagem_w	bigint;
nr_seq_lib_acesso_w		bigint;
ie_lib_acesso_w			varchar(1);

 

BEGIN 
 
if (cd_paciente_p IS NOT NULL AND cd_paciente_p::text <> '') then 
	select 	coalesce(max(nr_atendimento),0) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente 
	where	cd_pessoa_fisica	=	cd_paciente_p 
	and	coalesce(dt_alta::text, '') = '';
	 
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
		SELECT * FROM Consistir_acesso_pep( 	cd_estabelecimento_P, nr_atendimento_w, cd_paciente_p, cd_pessoa_fisica_p, nm_usuario_p, dt_logon_p, ie_permite_w, nr_seq_mensagem_w, nr_seq_lib_acesso_w, ie_lib_acesso_w) INTO STRICT ie_permite_w, nr_seq_mensagem_w, nr_seq_lib_acesso_w, ie_lib_acesso_w;
	else 
		SELECT * FROM Consistir_acesso_pep(	cd_estabelecimento_P, 0, cd_paciente_p, cd_pessoa_fisica_p, nm_usuario_p, dt_logon_p, ie_permite_w, nr_seq_mensagem_w, nr_seq_lib_acesso_w, ie_lib_acesso_w) INTO STRICT ie_permite_w, nr_seq_mensagem_w, nr_seq_lib_acesso_w, ie_lib_acesso_w;
	end if;
	 
	ie_permite_p	 	:= 	ie_permite_w;
	nr_seq_mensagem_p 	:=	nr_seq_mensagem_w;
end if;
 
end;
$body$
