CREATE OR REPLACE PROCEDURE onc_trocar_estabelecimento ( nr_seq_paciente_p bigint, nr_ciclo_p bigint, ds_dia_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (nr_ciclo_p IS NOT NULL AND nr_ciclo_p::text <> '') and (coalesce(ds_dia_p::text, '') = '') then
	update	paciente_atendimento
	set 	cd_estabelecimento = cd_estabelecimento_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_paciente = nr_seq_paciente_p
	and 	nr_ciclo = nr_ciclo_p
	and 	CASE WHEN coalesce(dt_liberacao_atend::text, '') = '' THEN 'N'  ELSE 'S' END   <> 'S';
end if;

if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (nr_ciclo_p IS NOT NULL AND nr_ciclo_p::text <> '') and (ds_dia_p IS NOT NULL AND ds_dia_p::text <> '') then
	update	paciente_atendimento
	set 	cd_estabelecimento = cd_estabelecimento_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_paciente = nr_seq_paciente_p
	and 	nr_ciclo = nr_ciclo_p
	and 	ds_dia_ciclo  = ds_dia_p
	and 	CASE WHEN coalesce(dt_liberacao_atend::text, '') = '' THEN 'N'  ELSE 'S' END   <> 'S';
end if;



commit;

end;
