SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE opme_consistencia_generica ( nr_seq_agenda_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE



qt_existe_w	bigint;
ds_mensagem_w	varchar(255);


BEGIN

ds_mensagem_p	:= null;

select	count(*)
into STRICT	qt_existe_w
from	agenda_paciente
where	nr_sequencia	= nr_seq_agenda_p
and	ie_opme_integracao <> 'S';
if (qt_existe_w = 1) then
	ds_mensagem_w	:= wheb_mensagem_pck.get_texto(802939) || nr_seq_agenda_p;
end if;

select	count(*)
into STRICT	qt_existe_w
from	agenda_paciente
where	nr_sequencia	= nr_seq_agenda_p;
if (qt_existe_w = 0) then
	ds_mensagem_w	:= wheb_mensagem_pck.get_texto(802944) || nr_seq_agenda_p;
end if;


if (coalesce(ds_mensagem_w, 'X') <> 'X') then
	ds_mensagem_p	:= substr(ds_mensagem_w,1,255);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
