CREATE OR REPLACE PROCEDURE ops_copiar_dic_objeto ( cd_funcao_p bigint, nr_seq_objeto_p bigint, nm_usuario_p text, ie_dinamic_form_p text default 'N', nr_seq_criado_p INOUT bigint DEFAULT NULL, ie_html_p text default 'N') AS $body$
DECLARE


ie_tipo_objeto_w	varchar(15);
nr_seq_criado_w		bigint;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_seq_objeto_p IS NOT NULL AND nr_seq_objeto_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	max(ie_tipo_objeto)
	into STRICT	ie_tipo_objeto_w
	from	dic_objeto
	where	nr_sequencia = nr_seq_objeto_p;

	if (ie_tipo_objeto_w = 'M') then
		begin
		nr_seq_criado_w := ops_copiar_dic_objeto_menu(cd_funcao_p, nr_seq_objeto_p, nm_usuario_p, nr_seq_criado_w);
		end;
	elsif (ie_tipo_objeto_w = 'C') then
		begin
		nr_seq_criado_w := copiar_dic_objeto_aba(cd_funcao_p, nr_seq_objeto_p, nm_usuario_p, nr_seq_criado_w, ie_html_p);
		end;
	elsif (ie_tipo_objeto_w = 'WF') then
		begin
		nr_seq_criado_w := copiar_dic_objeto_filtro(cd_funcao_p, nr_seq_objeto_p, nm_usuario_p, ie_dinamic_form_p, nr_seq_criado_w, ie_html_p);
		end;
	elsif (ie_tipo_objeto_w = 'WDF') then
		begin
		nr_seq_criado_w := copiar_dic_objeto_filtro(cd_funcao_p, nr_seq_objeto_p, nm_usuario_p, 'S', nr_seq_criado_w, ie_html_p);
		end;
	elsif (ie_tipo_objeto_w = 'A') then
		begin
		nr_seq_criado_w := COPIAR_DIC_OBJETO_JTREE(cd_funcao_p, nr_seq_objeto_p, nm_usuario_p, nr_seq_criado_w);
		end;
	elsif (ie_tipo_objeto_w = 'CD') then
		begin
		nr_seq_criado_w := COPIAR_DIC_OBJETO_WDETALHE(cd_funcao_p, nr_seq_objeto_p, nm_usuario_p, nr_seq_criado_w);
		end;
	elsif (ie_tipo_objeto_w = 'WCS') then
		begin
		nr_seq_criado_w := copiar_dic_objeto_grafico(cd_funcao_p, nr_seq_objeto_p, nm_usuario_p, nr_seq_criado_w);
		end;
	elsif (ie_tipo_objeto_w = 'WLV') then
		begin
		nr_seq_criado_w := COPIAR_DIC_OBJETO_WLISTVIEW(cd_funcao_p, nr_seq_objeto_p, nm_usuario_p, nr_seq_criado_w);
		end;
	elsif (ie_tipo_objeto_w = 'CLCB') then
		begin
		nr_seq_criado_w := ops_copiar_dic_objeto_loockup(cd_funcao_p, nr_seq_objeto_p, nm_usuario_p, nr_seq_criado_w);
		end;
	else
		begin

		select	nextval('dic_objeto_seq')
		into STRICT	nr_seq_criado_w
		;

		insert into dic_objeto(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo_objeto,
			nm_objeto,
			cd_funcao,
			ie_layout_xy,
			ds_texto,
			qt_altura,
			qt_compensacao_x,
			qt_compensacao_y,
			cd_exp_texto)
		SELECT	nr_seq_criado_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			CASE WHEN coalesce(ie_dinamic_form_p, 'N')='N' THEN  ie_tipo_objeto  ELSE 'WDF' END ,
			nm_objeto,
			cd_funcao_p,
			ie_layout_xy,
			ds_texto,
			qt_altura,
			qt_compensacao_x,
			qt_compensacao_y,
			cd_exp_texto
		from	dic_objeto
		where	nr_sequencia = nr_seq_objeto_p;
		end;
	end if;
	end;
end if;
nr_seq_criado_p	:= nr_seq_criado_w;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
