CREATE OR REPLACE PROCEDURE ops_criar_funcao_schematic ( cd_funcao_p bigint, nm_usuario_p text, nr_funcao_criada_p INOUT bigint, nr_seq_objeto_p INOUT bigint, nr_seq_funcao_java_p INOUT bigint) AS $body$
DECLARE


