nr_seq_obj_panel_w	 bigint;


BEGIN
IF (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') AND (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '')THEN
	BEGIN
	SELECT	cd_exp_funcao
	INTO STRICT	cd_exp_schematics_w
	FROM	funcao
	WHERE	cd_funcao = cd_funcao_p;

	SELECT nextval('funcao_schematic_seq')
	INTO STRICT nr_funcao_criada_w
	;

	INSERT INTO funcao_schematic(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_funcao,
		cd_exp_schematic
	) VALUES (
		nr_funcao_criada_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_funcao_p,
		cd_exp_schematics_w
	);

	COMMIT;

	SELECT	nextval('objeto_schematic_seq')
	INTO STRICT	nr_sequencia_w
	;

	INSERT INTO OBJETO_SCHEMATIC(
