		 CD_EXP_DESC_OBJ,
		 NR_SEQ_ATIVACAO,
		 NR_SEQ_OBJ_PADRAO,
		 IE_TIPO_OBJ_BUTTON,
		 NR_SEQ_OBJ_SUP_ATIV,
		 IE_HERDAR_PERMISSOES,
