CREATE OR REPLACE PROCEDURE orc_pac_importar_solic_ext_pep ( nr_seq_orcamento_p bigint, nr_seq_solicitacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
qt_procedimento_w	bigint;
nr_seq_exame_w		bigint;

c01 CURSOR FOR
	SELECT	cd_procedimento,
            ie_origem_proced,
            qt_exame,
            nr_seq_exame_lab
	from	pedido_exame_externo_item
	where	nr_seq_pedido = nr_seq_solicitacao_p
	and	    (cd_procedimento IS NOT NULL AND cd_procedimento::text <> '');


BEGIN

open c01;
loop
fetch 	c01 into
        cd_procedimento_w,
        ie_origem_proced_w,
        qt_procedimento_w,
        nr_seq_exame_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into ORCAMENTO_PACIENTE_PROC(
            nr_sequencia,
            nr_sequencia_orcamento,
            cd_procedimento,
            ie_origem_proced,
            dt_atualizacao,
            nm_usuario,
            qt_procedimento,
            nr_seq_exame,
            ie_valor_informado,
            vl_procedimento)
	values (
            nextval('orcamento_paciente_proc_seq'),
            nr_seq_orcamento_p,
            cd_procedimento_w,
            ie_origem_proced_w,
            clock_timestamp(),
            nm_usuario_p,
            coalesce(qt_procedimento_w,1),
            nr_seq_exame_w,
            'N',
            0);
	exception
	when 	others then
		nr_sequencia_w := null;
	end;
end loop;
close c01;

commit;

end;
$body$
