SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ordenar_lic_item (NR_SEQ_LIC_P bigint) AS $body$
DECLARE


NR_SEQ_W		LIC_ITEM.NR_SEQUENCIA%type;
DS_MATERIAL_W		varchar(254);
CONT_W			bigint;

C01 CURSOR FOR
SELECT		NR_SEQUENCIA,
		SUBSTR(OBTER_DESC_MATERIAL(CD_MATERIAL),1,100) DS_MATERIAL
FROM		LIC_ITEM
WHERE		NR_SEQ_LICITACAO = NR_SEQ_LIC_P
ORDER BY	DS_MATERIAL;


BEGIN
CONT_W := 0;
OPEN C01;
LOOP FETCH C01 INTO
	NR_SEQ_W,
	DS_MATERIAL_W;
EXIT WHEN NOT FOUND; /* apply on C01 */
	CONT_W := CONT_W + 1;
	UPDATE 	LIC_ITEM
	SET	NR_SEQ_ITEM = CONT_W
	WHERE	NR_SEQUENCIA = NR_SEQ_W;
	COMMIT;
END LOOP;
CLOSE C01;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
