CREATE OR REPLACE PROCEDURE osi_definir_classif_os (nr_OS_p bigint, nr_seq_osi_regra_resultado_p text) AS $body$
DECLARE


ie_classificacao_nova_w			man_ordem_servico.ie_classificacao%type;


BEGIN

if (nr_seq_osi_regra_resultado_p IS NOT NULL AND nr_seq_osi_regra_resultado_p::text <> '') then
	select	max(ie_classificacao)
	into STRICT	ie_classificacao_nova_w
	from	osi_regra_resultado
	where	nr_sequencia = nr_seq_osi_regra_resultado_p;

	if (ie_classificacao_nova_w IS NOT NULL AND ie_classificacao_nova_w::text <> '') then
		update	man_ordem_servico
		set	ie_classificacao = ie_classificacao_nova_w,
			ie_classificacao_cliente = coalesce(ie_classificacao_cliente,ie_classificacao)
		where	nr_sequencia = nr_OS_p;

		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
