CREATE OR REPLACE PROCEDURE os_registrar_troca_solicitante (NR_SEQ_ORDEM_SERV_P bigint, DS_RELAT_TECNICO_P text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_hist_w	bigint;
nr_projeto_w	integer;


BEGIN

if (nr_seq_ordem_serv_p IS NOT NULL AND nr_seq_ordem_serv_p::text <> '') then

	select count(*)
	into STRICT   nr_projeto_w
	from   proj_projeto
	where 1 = 1
	and	(NR_SEQ_ORDEM_SERV IS NOT NULL AND NR_SEQ_ORDEM_SERV::text <> '')
	and NR_SEQ_ORDEM_SERV = NR_SEQ_ORDEM_SERV_P;

	if (nr_projeto_w > 0) then

		select 	nextval('man_ordem_serv_tecnico_seq')
		into STRICT	nr_seq_hist_w
		;

		if (nr_seq_hist_w IS NOT NULL AND nr_seq_hist_w::text <> '')then

			insert into 	man_ordem_serv_tecnico(
							nr_sequencia,
							nr_seq_ordem_serv,
							dt_atualizacao,
							dt_historico,
							dt_liberacao,
							nm_usuario,
							nr_seq_tipo,
							ie_origem,
							ds_relat_tecnico)
			values (nr_seq_hist_w,
							nr_seq_ordem_serv_p,
							clock_timestamp(),
							clock_timestamp(),
							clock_timestamp(),
							nm_usuario_p,
							7,
							'I',
							ds_relat_tecnico_p);

			commit;
		end if;
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
