CREATE OR REPLACE PROCEDURE paf_gravar_chaves_priv_publica (cd_chave_publica_p text, cd_chave_privada_p text, cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE


cd_chave_publica_w	varchar(500);
cd_chave_privada_w	varchar(500);
qt_registro_w		bigint;

BEGIN

select	count(*)
into STRICT	qt_registro_w
from	paf_parametros
where	cd_estabelecimento = cd_estabelecimento_p;

if (qt_registro_w > 0) then
	select	cd_chave_publica,
		cd_chave_privada
	into STRICT	cd_chave_publica_w,
		cd_chave_privada_w
	from	paf_parametros
	where	cd_estabelecimento = cd_estabelecimento_p;
end if;

if (coalesce(cd_chave_publica_w::text, '') = '') and (coalesce(cd_chave_privada_w::text, '') = '') then
	if (qt_registro_w > 0) then
		update 	paf_parametros
		set	cd_chave_publica = cd_chave_publica_p,
			cd_chave_privada = cd_chave_privada_p,
			dt_atualizacao   = clock_timestamp(),
			nm_usuario       = nm_usuario_p
		where	cd_estabelecimento = cd_estabelecimento_p;
	else
		insert into paf_parametros(nr_sequencia,
					    dt_atualizacao,
					    nm_usuario,
					    dt_atualizacao_nrec,
					    nm_usuario_nrec,
					    cd_chave_publica,
					    cd_chave_privada,
					    cd_estabelecimento)
		values (nextval('paf_parametros_seq'),
					    clock_timestamp(),
					    nm_usuario_p,
					    clock_timestamp(),
					    nm_usuario_p,
					    cd_chave_publica_p,
					    cd_chave_privada_p,
					    cd_estabelecimento_p);
	end if;
end if;


commit;

end;
$body$
