CREATE OR REPLACE PROCEDURE paf_obter_param_usuario ( cd_funcao_p bigint, nr_sequencia_p bigint, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, vl_parametro_p INOUT text) AS $body$
BEGIN

select	vl_parametro
into STRICT	vl_parametro_p
from 	funcao_param_usuario
where 	cd_funcao		= cd_funcao_p
and 	nr_sequencia 		= nr_sequencia_p
and 	upper(nm_usuario_param) = upper(nm_usuario_p)
and	cd_estabelecimento	= cd_estabelecimento_p;
exception
	when others then
		begin

		select	vl_parametro
		into STRICT	vl_parametro_p
		from	funcao_param_perfil
		where	cd_funcao	= cd_funcao_p
		and	nr_sequencia	= nr_sequencia_p
		and	cd_perfil    	= cd_perfil_p;
		exception
			when others then
				begin
				select	vl_parametro
				into STRICT	vl_parametro_p
				from	funcao_param_estab
				where	cd_funcao		= cd_funcao_p
				and	nr_seq_param 		= nr_sequencia_p
				and	cd_estabelecimento	= cd_estabelecimento_p;
				exception
					when others then
						begin
					    	select	CASE WHEN coalesce(vl_parametro::text, '') = '' THEN  vl_parametro_padrao  ELSE vl_parametro END
			      			into STRICT 	vl_parametro_p
						from 	funcao_parametro
					    	where 	cd_funcao	= cd_funcao_p
			      			and 	nr_sequencia 	= nr_sequencia_p;
					    	exception
	    						when others then
								vl_parametro_p	:= 'S';
					    end;
				end;
		end;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
