CREATE OR REPLACE PROCEDURE pagador_before_post_eup_js ( nr_atendimento_p bigint, cd_estabelecimento_p bigint, ie_exige_cpf_pagador_p INOUT text, ds_texto_p INOUT text) AS $body$
DECLARE

 
cd_convenio_w		integer;
ie_exige_cpf_pagador_w	varchar(1);
ds_texto_w		varchar(255);


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')then 
	begin 
	 
	cd_convenio_w	:= obter_convenio_atendimento(nr_atendimento_p);
	 
	select coalesce(max(ie_exige_cpf_pagador), 'N') 
	into STRICT	ie_exige_cpf_pagador_w 
	from  convenio_estabelecimento 
	where  cd_convenio = cd_convenio_w 
	and   cd_estabelecimento = cd_estabelecimento_p;
	 
	ds_texto_w	:= wheb_mensagem_pck.get_texto(68279);
	 
	end;
end if;
 
ie_exige_cpf_pagador_p	:= ie_exige_cpf_pagador_w;
ds_texto_p		:= ds_texto_w;
 
end;
$body$
LANGUAGE PLPGSQL
