SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE palm_gerar_movimentacao_pa ( NM_USUARIO_P text, NR_SEQ_LOCAL_PA_P bigint, NR_ATENDIMENTO_P bigint, DS_MSG_FINAL_P INOUT text) AS $body$
DECLARE


DS_LOCAL_PA_W		PA_LOCAL.DS_LOCAL%TYPE;
NM_PAC_ATUAL_W		varchar(60);
NR_ATEND_ATUAL_W	ATENDIMENTO_PACIENTE.NR_ATENDIMENTO%TYPE;
IE_CONTROLE_W		PA_GRUPO_LOCAL.IE_CONTROLE%TYPE;
nr_sequencia_w		PA_LOCAL.NR_SEQUENCIA%TYPE;
QT_REG_EXISTE_W		smallint;
DS_ERRO_W		varchar(255);

BEGIN

SELECT	max(NR_SEQUENCIA)
INTO STRICT	nr_sequencia_w
FROM	PA_LOCAL A
WHERE	A.CD_BARRAS = NR_SEQ_LOCAL_PA_P;

IF (coalesce(nr_sequencia_w,0) > 0) THEN
	SELECT	A.DS_LOCAL,
		coalesce(B.IE_CONTROLE, 'C') -- Coletivo
	INTO STRICT	DS_LOCAL_PA_W,
		IE_CONTROLE_W
	FROM pa_local a
LEFT OUTER JOIN pa_grupo_local b ON (A.NR_SEQ_GRUPO_PA = B.NR_SEQUENCIA)
WHERE A.NR_SEQUENCIA = nr_sequencia_w;	
	
	IF (IE_CONTROLE_W = 'I') THEN
		SELECT	coalesce(MAX(NR_ATENDIMENTO),0)
		INTO STRICT NR_ATEND_ATUAL_W
		FROM	ATENDIMENTO_PACIENTE A
		WHERE	A.NR_SEQ_LOCAL_PA = nr_sequencia_w
		AND coalesce(A.DT_ALTA::text, '') = '';

		SELECT	MAX(OBTER_NOME_PF(A.CD_PESSOA_FISICA))
		INTO STRICT	NM_PAC_ATUAL_W
		FROM	ATENDIMENTO_PACIENTE A
		WHERE	A.NR_SEQ_LOCAL_PA = nr_sequencia_w
		AND A.NR_ATENDIMENTO = NR_ATEND_ATUAL_W;
	END IF;

	
	IF (IE_CONTROLE_W = 'I' AND NR_ATEND_ATUAL_W > 0) THEN
		DS_MSG_FINAL_P := wheb_mensagem_pck.get_texto(791916,
						'DS_LOCAL_PA='||DS_LOCAL_PA_W ||
						';NM_PAC_ATUAL='||NM_PAC_ATUAL_W ||
						';NR_ATEND_ATUAL='||NR_ATEND_ATUAL_W);		
	ELSE
		DS_ERRO_W := TROCA_LOCAL_PACIENTE_PA(nr_sequencia_w, NR_ATENDIMENTO_P, 0,  --qt_tempo_previsto_p
					NM_USUARIO_P, wheb_mensagem_pck.get_texto(791937), DS_ERRO_W, 'N');
		IF (coalesce(DS_ERRO_W::text, '') = '') THEN
			DS_MSG_FINAL_P := wheb_mensagem_pck.get_texto(791938,'DS_LOCAL_PA='||DS_LOCAL_PA_W);			
		ELSE
			DS_MSG_FINAL_P := DS_ERRO_W;
		END IF;
	END IF;
ELSE
	DS_MSG_FINAL_P := wheb_mensagem_pck.get_texto(791939);
END IF;

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
