CREATE OR REPLACE PROCEDURE particular_conv_todos_usuario (nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_usuario_convidado_w	varchar(15);
cd_pessoa_convidado_w	varchar(10);

c01 CURSOR FOR
	SELECT 	a.nm_usuario,
		a.cd_pessoa_fisica
	FROM 	usuario a
	WHERE 	a.ie_situacao = 'A'
	and 	a.cd_estabelecimento = cd_estabelecimento_p
	and 	not exists (	SELECT 	1
				from 	agenda_tasy_convite b
				where 	a.nm_usuario = b.nm_usuario_convidado
				and 	b.nr_seq_agenda = nr_seq_agenda_p);

BEGIN

open C01;
loop
fetch C01 into
	nm_usuario_convidado_w,
	cd_pessoa_convidado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL gravar_convidados(nr_seq_agenda_p, nm_usuario_convidado_w, cd_pessoa_convidado_w, nm_usuario_p);
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
