CREATE OR REPLACE PROCEDURE pat_aprovar_doc_transf ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, ie_operacao_p text, nm_usuario_p text, dt_referencia_p timestamp default clock_timestamp()) AS $body$
DECLARE


ie_gerar_transf_bem_w		varchar(1);
ie_transferencia_w			varchar(1);
ie_valor_w			varchar(1);
nr_seq_tipo_w			bigint;


BEGIN

ie_gerar_transf_bem_w	:= coalesce(obter_valor_param_usuario(937, 44, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p), 'N');

update	pat_doc_transferencia
set	dt_aprovacao	= CASE WHEN ie_operacao_p='A' THEN clock_timestamp() WHEN ie_operacao_p='E' THEN null END ,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;


if (ie_gerar_transf_bem_w = 'S') then

	select	max(nr_seq_tipo)
	into STRICT	nr_seq_tipo_w
	from	pat_doc_transferencia
	where	nr_sequencia	= nr_sequencia_p;

	select	coalesce(max(a.ie_transferencia), 'N'),
		coalesce(max(a.ie_valor), 'N')
	into STRICT	ie_transferencia_w,
		ie_valor_w
	from	pat_tipo_historico a
	where	a.nr_sequencia	= nr_seq_tipo_w;

	if (ie_transferencia_w = 'S') then

		CALL pat_gerar_transf_docto(nr_sequencia_p, nm_usuario_p, clock_timestamp());

	end if;

	if (ie_valor_w in ('B','V')) then

		pat_gerar_baixa_doc_bem(nr_sequencia_p, nm_usuario_p,dt_referencia_p);

	end if;

end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
