SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pat_atualizar_centro_local ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_centro_custo_w		integer;
nr_seq_local_manutencao_w	bigint;
ie_atualiza_man_equipto_w	varchar(2);


BEGIN

select 	coalesce(obter_valor_param_usuario(937,99,obter_perfil_ativo,nm_usuario_p,obter_estabelecimento_ativo),'N')
into STRICT	ie_atualiza_man_equipto_w
;

select	coalesce(max(cd_centro_custo),0),
	coalesce(max(nr_seq_local_manutencao),0)
into STRICT	cd_centro_custo_w,
	nr_seq_local_manutencao_w
from	pat_local
where	nr_sequencia = nr_sequencia_p;

if (cd_centro_custo_w > 0) then
	update	pat_bem
	set	cd_centro_custo	= cd_centro_custo_w,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	cd_centro_custo	<> cd_centro_custo_w
	and	nr_seq_local		= nr_sequencia_p;
end if;

if (nr_seq_local_manutencao_w > 0 AND ie_atualiza_man_equipto_w = 'S') then
	update 	man_equipamento
	set 	cd_centro_custo = cd_centro_custo_w,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	coalesce(cd_centro_custo,0) <> cd_centro_custo_w
	and	nr_seq_local = nr_seq_local_manutencao_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
