SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pat_consiste_regra_vl_residual ( nr_seq_regra_p bigint, nr_seq_bem_p bigint, ie_acao_p text, vl_residual_p bigint, dt_inicio_vigencia_p timestamp, dt_fim_vigencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


/*
ie_acao_p
0 - Criação de regra
1 - Alteração de regra
2 - Exclusão de regra
*/
nr_sequencia_w		pat_valor_bem.nr_sequencia%type;
vl_contabil_w		pat_valor_bem.vl_contabil%type;
ie_existe_w		varchar(01) := 'N';
cd_estab_w		estabelecimento.cd_estabelecimento%type;


BEGIN

if (coalesce(nr_seq_bem_p,0) = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(321841); /*Deve existir um bem selecionado para utilização da regra*/
end if;

if (nr_seq_regra_p > 0) then
	begin
	select	cd_estabelecimento
	into STRICT	cd_estab_w
	from	pat_bem
	where	nr_sequencia = nr_seq_bem_p;

	if (ie_acao_p in (0,1)) then
		begin
		if (coalesce(vl_residual_p,0) <= 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(321842); /*Não é possível salvar uma regra com valor residual igual ou menor que 0 (zero)*/
		end if;

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	pat_valor_bem
		where	nr_seq_bem = nr_seq_bem_p;

		if (coalesce(nr_sequencia_w,0) > 0) then
			select	vl_contabil
			into STRICT	vl_contabil_w
			from	pat_valor_bem
			where	nr_sequencia = nr_sequencia_w;
		else
			select	vl_original - coalesce(vl_residual,0)
			into STRICT	vl_contabil_w
			from	pat_bem
			where	nr_sequencia = nr_seq_bem_p;
		end if;

		if (coalesce(vl_residual_p,0) > 0) and (vl_residual_p > vl_contabil_w) then
			begin
			if (nr_sequencia_w > 0) then	/*Apenas para ver se teve alguma depreciação, pra montar a mensagem de acordo*/
				CALL wheb_mensagem_pck.exibir_mensagem_abort(321843); /*Não é possível salvar uma regra com valor residual superior ao valor contábil atual do bem*/
			else
				CALL wheb_mensagem_pck.exibir_mensagem_abort(321845); /*Não é possível salvar uma regra com valor residual superior ao valor original do bem*/
			end if;
			end;
		end if;
		end;
	end if;

	begin
	select	'S'
	into STRICT	ie_existe_w
	from	pat_mes_ref
	where	cd_estabelecimento = cd_estab_w
	and	dt_mes_ref between trunc(dt_inicio_vigencia_p,'mm') and fim_mes(coalesce(dt_fim_vigencia_p,dt_inicio_vigencia_p))
	and	ie_situacao = 1  LIMIT 1;
	exception
	when others then
		ie_existe_w := 'N';
	end;

	if (ie_existe_w = 'S') then
		begin
		if (ie_acao_p = '2') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(321846); /*Não é possível excluir uma regra com o mês calculado, é necessário desfazer a depreciação*/
		else
			CALL wheb_mensagem_pck.exibir_mensagem_abort(321847); /*Não é possível salvar uma regra com o mês calculado, é necessário desfazer a depreciação*/
		end if;
		end;
	end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pat_consiste_regra_vl_residual ( nr_seq_regra_p bigint, nr_seq_bem_p bigint, ie_acao_p text, vl_residual_p bigint, dt_inicio_vigencia_p timestamp, dt_fim_vigencia_p timestamp, nm_usuario_p text) FROM PUBLIC;

