SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pat_enviar_comunic_ficha_aprov ( cd_estabelecimento_p bigint, nr_seq_ficha_p text, nr_seq_bem_p bigint, nm_usuario_dest_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


cd_perfil_adicional_w			varchar(10);
cd_bem_w				varchar(20);
ds_titulo_w				varchar(255);
ds_tipo_bem_w				varchar(255);
ds_comunicado_w				varchar(4000);
ds_bem_w				varchar(255);
ds_local_w				varchar(255);
nr_seq_tipo_w				bigint;
nr_seq_bem_w				bigint;


BEGIN

/*
FP -	comunicado de ficha de aceite pendente para aprovação.
FA -	comunicado de ficha de aceite aprovada.
*/
ds_titulo_w		:= '';
ds_comunicado_w		:= '';

cd_perfil_adicional_w	:= coalesce(substr(obter_valor_param_usuario(937, 55, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p), 1, 10), 'X');

select	max(substr(pat_obter_dados_local(nr_seq_local, 'D'), 1, 255)),
	max(nr_seq_bem)
into STRICT	ds_local_w,
	nr_seq_bem_w
from	pat_ficha_aceite
where	nr_sequencia	= nr_seq_ficha_p;


nr_seq_bem_w		:= coalesce(nr_seq_bem_p, nr_seq_bem_w);
select	max(cd_bem),
	max(ds_bem),
	max(nr_seq_tipo),
	max(substr(pat_obter_desc_tipo_bem(nr_seq_tipo), 1, 255))
into STRICT	cd_bem_w,
	ds_bem_w,
	nr_seq_tipo_w,
	ds_tipo_bem_w
from	pat_bem
where	nr_sequencia 	= nr_seq_bem_w;


if (cd_bem_w IS NOT NULL AND cd_bem_w::text <> '') then


	if (ie_opcao_p = 'FP') then

		ds_titulo_w		:= substr(	wheb_mensagem_pck.get_texto(799543), 1, 255);
		ds_comunicado_w		:= substr(	wheb_mensagem_pck.get_texto(799549)|| chr(13) || chr(10) ||
						wheb_mensagem_pck.get_texto(799489) || ' ' || chr(9) || chr(9) || cd_bem_w 	|| ' - ' || ds_bem_w || chr(13) || chr(10) ||
						wheb_mensagem_pck.get_texto(799490) || ' ' || chr(9) || chr(9) || nr_seq_tipo_w	|| ' - ' || ds_tipo_bem_w || chr(13) || chr(10) ||
						wheb_mensagem_pck.get_texto(799491) || ' ' || chr(9) || ds_local_w 	|| '.' , 1, 4000);
	elsif (ie_opcao_p = 'FA') then

		ds_titulo_w		:= substr(	wheb_mensagem_pck.get_texto(799547), 1, 255);
		ds_comunicado_w		:= substr(	wheb_mensagem_pck.get_texto(799657)			|| chr(13) || chr(10) ||
							wheb_mensagem_pck.get_texto(799489) || ' ' || chr(9) || chr(9) || cd_bem_w 	|| ' - ' || ds_bem_w || chr(13) || chr(10) ||
							wheb_mensagem_pck.get_texto(799490) || ' ' || chr(9) || chr(9) || nr_seq_tipo_w	|| ' - ' || ds_tipo_bem_w || chr(13) || chr(10) ||
							wheb_mensagem_pck.get_texto(799491) || ' ' || chr(9) || ds_local_w 	|| '.' , 1, 4000);
	elsif (ie_opcao_p = 'FR') then

		ds_titulo_w		:= substr(	wheb_mensagem_pck.get_texto(799547), 1, 255);
		ds_comunicado_w		:= substr(	wheb_mensagem_pck.get_texto(799659)		|| chr(13) || chr(10) ||
						wheb_mensagem_pck.get_texto(799489) || ' ' || chr(9) || chr(9) || cd_bem_w 	|| ' - ' || ds_bem_w || chr(13) || chr(10) ||
						wheb_mensagem_pck.get_texto(799490) || ' ' || chr(9) || chr(9) || nr_seq_tipo_w	|| ' - ' || ds_tipo_bem_w || chr(13) || chr(10) ||
						wheb_mensagem_pck.get_texto(799491) || ' ' || chr(9) || ds_local_w 	|| '.' , 1, 4000);
	end if;

	if (coalesce(ds_titulo_w, 'X') <> 'X') then

		CALL gerar_comunic_padrao(	clock_timestamp(),
					ds_titulo_w,
					ds_comunicado_w,
					nm_usuario_p,
					null,
					nm_usuario_dest_p,
					'N',
					null,
					cd_perfil_adicional_w,
					cd_estabelecimento_p,
					null,
					clock_timestamp(),
					null,
					null);
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pat_enviar_comunic_ficha_aprov ( cd_estabelecimento_p bigint, nr_seq_ficha_p text, nr_seq_bem_p bigint, nm_usuario_dest_p text, ie_opcao_p text, nm_usuario_p text) FROM PUBLIC;

