CREATE OR REPLACE PROCEDURE pat_gerar_ficha_aceite ( cd_estabelecimento_p bigint, nr_seq_bem_p bigint, ie_origem_p text, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_resp_w			varchar(10);
ie_gerar_ficha_w			varchar(01)	:= 'N';
nr_seq_local_w			bigint;
nm_usuario_resp_w			varchar(255);
nr_seq_ficha_w			bigint;


BEGIN

ie_gerar_ficha_w		:= substr(coalesce(pat_obter_se_ficha_aceite(cd_estabelecimento_p, nr_seq_bem_p), 'N'),1,1);

if (ie_gerar_ficha_w = 'S') then
	begin
	select	max(nr_seq_local)
	into STRICT	nr_seq_local_w
	from	pat_bem
	where	nr_sequencia	= nr_seq_bem_p;

	select	coalesce(max(cd_pf_resp_local),'0')
	into STRICT	cd_pessoa_resp_w
	from	pat_local
	where	nr_sequencia	= nr_seq_local_w;

	if (cd_pessoa_resp_w <> '0') then
		begin

		select	nextval('pat_ficha_aceite_seq')
		into STRICT	nr_seq_ficha_w
		;

		insert into pat_ficha_aceite(
			nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			cd_pessoa_fisica,
			dt_registro,
			nr_seq_bem,
			ie_origem,
			nr_seq_local)
		values (	nr_seq_ficha_w,
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			cd_pessoa_resp_w,
			clock_timestamp(),
			nr_seq_bem_p,
			ie_origem_p,
			nr_seq_local_w);
		end;

		nm_usuario_resp_w	:= substr(obter_usuario_pessoa(cd_pessoa_resp_w),1,255);
		if (coalesce(nm_usuario_resp_w, 'X') <> 'X') then

			/*Avisar que o responsavel da localização, sobre a existência uma ficha de
			aceite pendente para aprovação*/
			CALL pat_enviar_comunic_ficha_aprov(	cd_estabelecimento_p,
							nr_seq_ficha_w,
							nr_seq_bem_p,
							nm_usuario_resp_w,
							'FP',
							nm_usuario_p);

		end if;
	end if;

	if (ie_commit_p = 'S') then
		commit;
	end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
