SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pat_gerar_man_localizacao ( nr_seq_local_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_local_manutencao_w		bigint;
nr_sequencia_w			bigint;

BEGIN

select	coalesce(max(nr_seq_local_manutencao),0)
into STRICT	nr_seq_local_manutencao_w
from	pat_local
where	nr_sequencia	= nr_seq_local_p;

if (nr_seq_local_manutencao_w = 0) then

	select	nextval('man_localizacao_seq')
	into STRICT	nr_sequencia_w
	;

	insert into man_localizacao(
		nr_sequencia,
		ds_localizacao,
		cd_estabelecimento,
		cd_setor,
		dt_atualizacao,
		nm_usuario,
		ie_terceiro,
		ie_calcula_valor,
		ie_situacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_aviso_os_prod,
		ie_gerar_ordem_prev,
		cd_cnpj,
		qt_aviso,
		ie_webservice,
		nr_seq_apres,
		ie_email,
		cd_localizacao,
		cd_empresa,
		nr_seq_andar,
		nr_seq_bloco,
		cd_centro_custo,
		cd_classificacao,
		ie_controle_diario,
		qt_metragem)
	SELECT	nr_sequencia_w,
		substr(ds_local,1,80) ds_local,
		cd_estabelecimento,
		cd_setor_atendimento,
		clock_timestamp(),
		nm_usuario_p,
		'N',
		'N',
		'A',
		clock_timestamp(),
		nm_usuario_p,
		'N',
		'N',
		'',
		null,
		'N',
		null,
		'S',
		cd_local,
		obter_empresa_estab(cd_estabelecimento),
		'',
		'',
		cd_centro_custo,
		cd_classificacao,
		'N',
		qt_metragem
	from	pat_local
	where	nr_sequencia	= nr_seq_local_p;

	update	pat_local
	set	nr_seq_local_manutencao	= nr_sequencia_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_local_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
