SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pat_gerar_man_tipo_equip ( nr_seq_tipo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tipo_equip_w			bigint;
nr_sequencia_w				bigint;

BEGIN

select	coalesce(max(nr_seq_tipo_equip),0)
into STRICT	nr_seq_tipo_equip_w
from	pat_tipo_bem
where	nr_sequencia	= nr_seq_tipo_p;

if (nr_seq_tipo_equip_w = 0) then

	select	nextval('man_tipo_equipamento_seq')
	into STRICT	nr_sequencia_w
	;

	insert into man_tipo_equipamento(
		nr_sequencia,
		ds_tipo_equip,
		dt_atualizacao,
		nm_usuario,
		ie_voltagem,
		ie_ano_fabricacao,
		ie_marca,
		ie_modelo,
		ie_peso,
		qt_dia_intervalo,
		ie_situacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_envio_comunicacao,
		ie_mapeamento)
	SELECT	nr_sequencia_w,
		ds_tipo,
		clock_timestamp(),
		nm_usuario_p,
		'N',
		'N',
		'N',
		'N',
		'N',
		0,
		'A',
		clock_timestamp(),
		nm_usuario_p,
		'',
		'N'
	from	pat_tipo_bem
	where	nr_sequencia	= nr_seq_tipo_p;

	update	pat_tipo_bem
	set	nr_seq_tipo_equip	= nr_sequencia_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_tipo_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
