CREATE OR REPLACE PROCEDURE pat_gerar_nova_taxa_bem ( nr_seq_ajuste_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Calcular a taxa de depreciação do bem
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
	Lei 11638
-------------------------------------------------------------------------------------------------------------------
Referências:
	Controle de patrimônio
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
tx_deprec_novo_w		double precision;

c_ajuste_valor_bem CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_ajuste,
		vl_original,
		vl_deprec_acum,
		tx_deprec_novo,
		qt_tempo_vida_util
	from	pat_ajuste_valor_bem
	where	nr_seq_ajuste	= nr_seq_ajuste_p;

vet01 c_ajuste_valor_bem%rowtype;


BEGIN
open c_ajuste_valor_bem;
loop
fetch c_ajuste_valor_bem into
	vet01;
EXIT WHEN NOT FOUND; /* apply on c_ajuste_valor_bem */
	begin
	tx_deprec_novo_w	:= dividir(100, vet01.qt_tempo_vida_util);

	update	pat_ajuste_valor_bem
	set	tx_deprec_novo	= tx_deprec_novo_w
	where	nr_sequencia	= vet01.nr_sequencia;
	end;
end loop;
close c_ajuste_valor_bem;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
