CREATE OR REPLACE PROCEDURE pat_gerar_taxa_bem_ajuste ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_mes_ref_w				timestamp;
qt_registro_w				bigint	:= 0;
c01 CURSOR FOR
SELECT	a.nr_seq_bem,
	a.tx_deprec,
	a.tx_deprec_novo
from	pat_ajuste_valor_bem a
where	a.nr_seq_ajuste		= nr_sequencia_p
and	a.tx_deprec		<> a.tx_deprec_novo;

vet01	C01%RowType;


BEGIN

select	max(dt_mes_ref)
into STRICT	dt_mes_ref_w
from	pat_ajuste_valor
where	nr_sequencia	= nr_sequencia_p;

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	qt_registro_w	:= qt_registro_w + 1;

	CALL gerar_pat_bem_taxa(	vet01.nr_seq_bem,
				dt_mes_ref_w,
				vet01.tx_deprec_novo,
				vet01.tx_deprec,
				nm_usuario_p);

	if (qt_registro_w >=1000) then
		commit;
		qt_registro_w	:= 0;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pat_gerar_taxa_bem_ajuste ( nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

