CREATE OR REPLACE PROCEDURE pat_liberar_ajuste_valor ( nr_sequencia_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


/*IE_OPERACAO
E = ESTORNAR
L = LIBERAR */
BEGIN

if (ie_operacao_p = 'E') then
	update 	pat_ajuste_valor
	set	dt_liberacao 	 = NULL,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
else
	update	pat_ajuste_valor
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
