CREATE OR REPLACE PROCEDURE pat_local_carga_inicial (nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w 		bigint;
ds_local_w		varchar(80);
cd_centro_custo_w	integer;
cd_setor_w		integer;
qt_registro_w		smallint :=0;

c01 CURSOR FOR
SELECT 	nr_sequencia,
	substr(ds_localizacao,1,80),
	cd_centro_custo,
	cd_setor
from 	man_localizacao
where 	ie_situacao = 'A'
and	cd_estabelecimento = cd_estabelecimento_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	ds_local_w,
	cd_centro_custo_w,
	cd_setor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select 	count(*)
	into STRICT	qt_registro_w
	from	pat_local
	where	nr_seq_local_manutencao = nr_sequencia_w
	and	ie_situacao = 'A'
	and	((cd_centro_custo <> cd_centro_custo_w) or (cd_setor_atendimento <> cd_setor_w));

	if (qt_registro_w > 0) then
		update 	pat_local
		set	cd_centro_custo = cd_centro_custo_w,
			cd_setor_atendimento = cd_setor_w
		where	nr_seq_local_manutencao = nr_sequencia_w
		and	ie_situacao = 'A'
		and	((cd_centro_custo <> cd_centro_custo_w) or (cd_setor_atendimento <> cd_setor_w));
	else

		CALL man_insert_pat_local(	nm_usuario_p,
					cd_centro_custo_w,
					cd_estabelecimento_p,
					cd_setor_w,
					ds_local_w,
					nr_sequencia_w);
	end if;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
